import React, { useEffect, useState } from 'react';
import './PlayerProfile.css';
import { backendApi } from '../services/backendApi';
import useTonConnect from '../hooks/useTonConnect';

const Stat = ({ label, value }) => (
  <div className="pp-stat">
    <div className="pp-label">{label}</div>
    <div className="pp-value">{value}</div>
  </div>
);

const PlayerProfile = () => {
  const { address, formattedAddress, isConnected } = useTonConnect();
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let active = true;
    async function load() {
      if (!address) {
        setStats(null);
        return;
      }
      setLoading(true);
      try {
        const data = await backendApi.fetchJson(`/api/stats/player/${encodeURIComponent(address)}`);
        if (!active) return;
        setStats(data);
      } catch (_) {
        if (!active) return;
        setStats(null);
      } finally {
        if (active) setLoading(false);
      }
    }
    load();
    const t = setInterval(load, 15000);
    return () => { active = false; clearInterval(t); };
  }, [address]);

  if (!isConnected) {
    return (
      <div className="player-profile">
        <div className="pp-header">Profile</div>
        <div className="pp-empty">Connect your wallet to see your stats.</div>
      </div>
    );
  }

  return (
    <div className="player-profile">
      <div className="pp-header">Profile</div>
      <div className="pp-sub">{formattedAddress || address}</div>
      {loading && <div className="pp-loading">Loadingâ€¦</div>}
      {stats ? (
        <div className="pp-grid">
          <Stat label="Total Bets" value={stats.totalBets ?? 0} />
          <Stat label="Amount Bet" value={(stats.totalAmountBet ?? 0).toFixed(3)} />
          <Stat label="Wins" value={stats.totalWins ?? 0} />
          <Stat label="Prize Won" value={(stats.totalPrize ?? 0).toFixed(3)} />
        </div>
      ) : (
        !loading && <div className="pp-empty">No stats yet. Place your first bet!</div>
      )}
    </div>
  );
};

export default PlayerProfile;
