import React, { useEffect, useState, memo } from 'react';
import './PlayerProfile.css';
import { backendApi } from '../services/backendApi';
import useTonConnect from '../hooks/useTonConnect';
import { TonIcon } from './IconComponents';
import Leaderboard from './Leaderboard';

const StatsView = ({ currentRound }) => {
  const { address, isConnected } = useTonConnect();
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    let active = true;
    async function load() {
      if (!address) {
        setStats(null);
        return;
      }
      setLoading(true);
      try {
        const data = await backendApi.fetchJson(`/api/stats/player/${encodeURIComponent(address)}`);
        if (!active) return;
        setStats(data);
      } catch (_) {
        if (!active) return;
        setStats(null);
      } finally {
        if (active) setLoading(false);
      }
    }
    load();
    const t = setInterval(load, 30000); // Reduced frequency
    return () => { active = false; clearInterval(t); };
  }, [address]);

  const referralEarnings = Number(stats?.referralEarnings ?? 0);
  const referralCount = Number(stats?.referralCount ?? 0);
  const referrer = stats?.referrer || null;

  const short = (addr) => {
    if (!addr || typeof addr !== 'string') return '';
    return `${addr.slice(0,6)}...${addr.slice(-6)}`;
  };

  return (
    <div className="stats-view">
      <Leaderboard currentRound={currentRound} />

      {isConnected && (
        <>
          <div className="pp-section-header" style={{ marginTop: 16 }}>
            <div className="pp-section-title">Referrals</div>
          </div>
          <div className="pp-summary">
            <div className="pp-card">
              <div className="pp-card-title">Referral Earnings</div>
              <div className="pp-amount pos">
                <TonIcon size={16} className="pp-amount-icon" />
                <span>{referralEarnings.toFixed(3)}</span>
              </div>
            </div>
            <div className="pp-card">
              <div className="pp-card-title">Referrals</div>
              <div className="pp-amount"><span>{referralCount}</span></div>
            </div>
          </div>
          <div className="pp-card">
            <div className="pp-card-title">Your Referrer</div>
            <div className="pp-referrer">{referrer ? short(referrer) : '—'}</div>
          </div>
          {loading && <div className="pp-loading">Loading…</div>}
        </>
      )}
    </div>
  );
};

export default memo(StatsView);
