import { useState, useEffect } from 'react';
import apiService from '../services/apiService';

const useOnlineCount = () => {
  const [onlineCount, setOnlineCount] = useState(0);
  const [isLoading, setIsLoading] = useState(false);

  const fetchOnlineCount = async () => {
    setIsLoading(true);
    try {
      console.log('ðŸ” Fetching online count...');
      const stats = await apiService.getServerStats();
      console.log('ðŸ“Š Server stats received:', stats);
      setOnlineCount(stats.connectedUsers || 0);
    } catch (error) {
      console.error('Failed to fetch online count:', error);
      setOnlineCount(0);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    // Fetch initial count
    fetchOnlineCount();

    // Update every 30 seconds
    const interval = setInterval(fetchOnlineCount, 30000);

    return () => clearInterval(interval);
  }, []);

  return { onlineCount, isLoading, refetch: fetchOnlineCount };
};

export default useOnlineCount;
