import React from 'react';
import { Volume2, VolumeX, User, MessageCircle } from 'lucide-react';
import WalletConnection from './WalletConnection';
import SoundControl from './SoundControl';
import './Header.css';

const Header = ({ onShowUsernameInput, currentUsername }) => {
  const [onlineUsers, setOnlineUsers] = useState(0);
  const [isConnected, setIsConnected] = useState(false);

  // Check if user has a proper username (not a fallback)
  const hasProperUsername = currentUsername && 
    currentUsername.trim().length >= 2 && 
    !currentUsername.startsWith('Player_');

  // Listen to socket connection and online users
  useEffect(() => {
    const handleConnect = () => {
      setIsConnected(true);
    };

    const handleDisconnect = () => {
      setIsConnected(false);
      setOnlineUsers(0);
    };

    const handleUserJoined = (data) => {
      setOnlineUsers(data.totalUsers);
    };

    const handleUserLeft = (data) => {
      setOnlineUsers(data.totalUsers);
    };

    // Subscribe to socket events
    socketService.on('connect', handleConnect);
    socketService.on('disconnect', handleDisconnect);
    socketService.on('user:joined', handleUserJoined);
    socketService.on('user:left', handleUserLeft);

    // Get initial connection status
    setIsConnected(socketService.isConnected);

    return () => {
      socketService.off('connect', handleConnect);
      socketService.off('disconnect', handleDisconnect);
      socketService.off('user:joined', handleUserJoined);
      socketService.off('user:left', handleUserLeft);
    };
  }, []);

  return (
    <div className="page-header">
      <div className="header-content">
        <div className="header-left">
          <img src="/logo.jpg" alt="SlotPot Logo" className="logo-image" />
          <div className="app-title">SlotPot</div>
        </div>

        <div className="header-right">
          <div className="online-indicator-header">
            <div className={`online-dot ${isConnected ? 'connected' : 'disconnected'}`}></div>
            <span className="online-count">{onlineUsers}</span>
            <Users size={14} className="online-icon" />
          </div>
          <SoundControl />
          {!hasProperUsername && onShowUsernameInput && (
            <button 
              className="username-setup-btn"
              onClick={onShowUsernameInput}
              title="Set your username"
            >
              <User size={16} />
              <span>Set Username</span>
            </button>
          )}
          <button 
            className="chat-bubble-btn"
            onClick={() => window.open('https://t.me/YumeOnTon', '_blank')}
            title="Join our Telegram chat"
          >
            <MessageCircle size={16} />
          </button>
          <WalletConnection />
        </div>
      </div>
    </div>
  );
};

export default Header;
