import React, { useState, useEffect, useRef } from 'react';
import { TonConnectUIProvider } from '@tonconnect/ui-react';
import Chat from './components/Chat';
import GameArea from './components/GameArea';
import MobileChat from './components/MobileChat';
import WalletConnection from './components/WalletConnection';
import useTelegramWebApp from './hooks/useTelegramWebApp';
import useJackpotContract from './hooks/useJackpotContract';
import socketService from './services/socketService';
import { tonConnectConfig, uiOptions } from './config/tonconnect';
import { MessageCircle, Trophy, Users, DollarSign } from 'lucide-react';
import './App.css';
import './components/Header.css';

// TON Connect manifest URL - use remote manifest to avoid 500 errors
const manifestUrl = "https://raw.githubusercontent.com/Vodka2134156/kzsks/main/manifest.json";

function AppContent() {
  // Local UI state
  const [currentRound, setCurrentRound] = useState(53408);
  const [timeRemaining, setTimeRemaining] = useState(30);
  const [activeTab, setActiveTab] = useState('jackpot');
  const [betAmount, setBetAmount] = useState(0.11);
  const [showWinnerAnnouncement, setShowWinnerAnnouncement] = useState(false);

  // Winner state management (moved from GameArea.js)
  const [waitingForWinner, setWaitingForWinner] = useState(false);
  const [contractWinner, setContractWinner] = useState(null);
  const [postWinnerLoading, setPostWinnerLoading] = useState(false);

  // Bettors state (managed centrally to avoid duplicates)
  const [gameBettors, setGameBettors] = useState([]);

  // Refs to avoid stale closures in socket handlers
  const gameBettorsRef = useRef([]);
  const contractWinnerRef = useRef(null);
  const showWinnerAnnouncementRef = useRef(false);
  const lastWinnerTimestampRef = useRef(null);

  // Keep refs in sync with state
  useEffect(() => { gameBettorsRef.current = gameBettors; }, [gameBettors]);
  useEffect(() => { contractWinnerRef.current = contractWinner; }, [contractWinner]);
  useEffect(() => { showWinnerAnnouncementRef.current = showWinnerAnnouncement; }, [showWinnerAnnouncement]);

  // Telegram integration
  const { user, hapticFeedback, showAlert, hasRealUserData } = useTelegramWebApp();

  // Smart contract integration
  const {
    contractState,
    userStats,
    adminInfo,
    isLoadingContract,
    isLoadingUserStats,
    isPlacingBet,
    placeBet,
    isConnected,
    address,
    currentBettors
  } = useJackpotContract();

  // Use real contract data or fallback to simulated data
  const jackpotValue = contractState.totalJackpot;
  const isLive = contractState.isActive;
  const userWinChance = parseFloat(userStats.winChancePercentage) || 0;
  const userBetTotal = userStats.betAmount || 0;

  // Use automation timer or fallback to UI timer
  useEffect(() => {
    // Always sync with backend timer when it changes
    if (contractState.timer?.timeRemaining !== undefined) {
      setTimeRemaining(contractState.timer.timeRemaining);
    } else {
      setTimeRemaining(30);
    }
  }, [contractState.timer?.timeRemaining]);

  // Local countdown timer (runs between backend updates)
  useEffect(() => {
    if (!isLive || timeRemaining <= 0) return;

    const countdown = setInterval(() => {
      setTimeRemaining(prev => {
        const newTime = Math.max(0, prev - 1);

        // If we reach 0, don't reset - let backend handle the transition
        if (newTime === 0) {
          console.log('â° Local timer reached 0, waiting for backend...');
        }

        return newTime;
      });
    }, 1000);

    return () => clearInterval(countdown);
  }, [isLive, timeRemaining > 0]); // Restart countdown when timer changes or game goes live

  // Socket integration for immediate synchronization - SINGLE CONNECTION POINT
  useEffect(() => {
    console.log('ðŸ”Œ App.js: Establishing single socket connection for entire app...');
    socketService.connect();

    // Listen for timer updates from socket
    socketService.on('timer', (timerData) => {
      console.log('â° App: Socket timer update:', timerData);
      setTimeRemaining(timerData.timeRemaining);
      if (timerData.roundNumber) {
        setCurrentRound(timerData.roundNumber);
      }
      // Note: Contract state timer will be updated via fullGameUpdate/contractStateUpdate events
    });

    // Listen for new round events
    socketService.on('newRound', (roundData) => {
      console.log('ðŸŽ° App: New round detected via socket:', roundData);
      setTimeRemaining(roundData.timeRemaining);
      setCurrentRound(roundData.roundNumber);
      lastWinnerTimestampRef.current = null; // Clear duplicate protection for new round
    });

    // Listen for game state updates
    socketService.on('gameState', (gameState) => {
      console.log('ðŸŽ® App: Game state update:', gameState);
      if (gameState.timeRemaining !== undefined) {
        setTimeRemaining(gameState.timeRemaining);
      }
      if (gameState.currentRound) {
        // Extract roundNumber if currentRound is an object, otherwise use the value directly
        const roundNumber = typeof gameState.currentRound === 'object' && gameState.currentRound.roundNumber 
          ? gameState.currentRound.roundNumber 
          : gameState.currentRound;
        setCurrentRound(roundNumber);
      }

      // Handle winner-related states
      if (gameState.isWaitingForWinner !== undefined) {
        setWaitingForWinner(gameState.isWaitingForWinner);
      }

      // Update bettors data centrally (avoids duplicate processing)
      if (gameState.bettors && Array.isArray(gameState.bettors)) {
        // Only log if bettors count changed to reduce spam
        if (gameState.bettors.length !== gameBettorsRef.current.length) {
          console.log('ðŸŽ® App: Updating bettors from gameState:', gameState.bettors.length, 'bettors');
        }
        setGameBettors(gameState.bettors);
      }

      // Check for winner announcement in game state (prevent duplicates with stronger protection)
      if (gameState.winnerAnnouncement && !showWinnerAnnouncementRef.current) {
        const winnerTimestamp = gameState.winnerAnnouncement.timestamp;

        // Check if this is the same winner announcement we already processed
        if (lastWinnerTimestampRef.current !== winnerTimestamp) {

          console.log('ðŸŽ‰ Winner announcement found in game state:', gameState.winnerAnnouncement);
          lastWinnerTimestampRef.current = winnerTimestamp;
          setContractWinner(gameState.winnerAnnouncement);
          setShowWinnerAnnouncement(true);
          setWaitingForWinner(false);

          // Auto-hide after 6 seconds
          setTimeout(() => {
            setShowWinnerAnnouncement(false);
            setContractWinner(null);
          }, 6000);
        } else {
          console.log('ðŸ”„ Duplicate winner announcement skipped (gameState) - already processed timestamp:', winnerTimestamp);
        }
      }
    });

    // Listen for comprehensive game data updates
    socketService.on('fullGameUpdate', (gameData) => {
      console.log('ðŸŽ® App: Full game update:', gameData);
      // These updates will trigger re-renders via useJackpotContract hook
      // The hook will detect the changes and update accordingly
    });

    // Listen for bettors updates
    socketService.on('bettorsUpdate', (data) => {
      console.log('ðŸ’° App: Bettors update:', data);
      // Force a refresh of contract state to sync with socket data
      // This ensures jackpot value and user stats stay in sync
    });

    // Listen for waiting for winner events
    socketService.on('waitingForWinner', (isWaiting) => {
      console.log('ðŸŽ¯ App: Waiting for winner state changed:', isWaiting);
      setWaitingForWinner(isWaiting);
    });

    // Listen for winner announcements (primary)
    socketService.on('winner', (winnerData) => {
      console.log('ðŸ† App: Winner announced:', winnerData);
      setWaitingForWinner(false);
      setPostWinnerLoading(true);

      // Show winner announcement immediately (if not already shown)
      const winnerTimestamp = winnerData.timestamp;
      if (lastWinnerTimestampRef.current !== winnerTimestamp) {
        console.log('ðŸ† Processing winner event');
        lastWinnerTimestampRef.current = winnerTimestamp;
        setContractWinner(winnerData);
        setShowWinnerAnnouncement(true);

        // Auto-hide after 6 seconds
        setTimeout(() => {
          setShowWinnerAnnouncement(false);
          setContractWinner(null);
          setPostWinnerLoading(false);
        }, 6000);
      } else {
        console.log('ðŸ”„ Duplicate winner event skipped');
      }
    });

    // Listen for direct winner announcements (fallback/reliable delivery)
    socketService.on('winnerAnnouncement', (winnerData) => {
      console.log('ðŸŽ¯ App: Direct winner announcement:', winnerData);
      setWaitingForWinner(false);

      // Ensure winner is shown even if other events were missed (duplicate protection)
      const winnerTimestamp = winnerData.timestamp;
      if (!showWinnerAnnouncementRef.current && lastWinnerTimestampRef.current !== winnerTimestamp) {
        console.log('ðŸŽ¯ Processing direct winner announcement');
        lastWinnerTimestampRef.current = winnerTimestamp;
        setContractWinner(winnerData);
        setShowWinnerAnnouncement(true);

        // Auto-hide after 6 seconds
        setTimeout(() => {
          setShowWinnerAnnouncement(false);
          setContractWinner(null);
        }, 6000);
      } else {
        console.log('ðŸ”„ Duplicate direct winner announcement skipped');
      }
    });

    // Listen for round resets
    socketService.on('roundReset', () => {
      console.log('ðŸ”„ App: Round reset');
      setWaitingForWinner(false);
      setPostWinnerLoading(false);
      setShowWinnerAnnouncement(false);
      setContractWinner(null);
      lastWinnerTimestampRef.current = null; // Clear duplicate protection for new round
    });

    return () => {
      console.log('ðŸ”Œ App.js: Cleaning up socket listeners...');
      socketService.off('timer');
      socketService.off('newRound');
      socketService.off('gameState');
      socketService.off('fullGameUpdate');
      socketService.off('bettorsUpdate');
      socketService.off('waitingForWinner');
      socketService.off('winner');
      socketService.off('winnerAnnouncement');
      socketService.off('roundReset');
      // Disconnect when app unmounts to prevent memory leaks
      socketService.disconnect();
    };
  }, []);

  const handleBetChange = (e) => {
    const value = parseFloat(e.target.value) || 0;
    setBetAmount(Math.max(0, Math.min(value, 10))); // Min 0, Max 10
  };

  const handleTabChange = (tab) => {
    hapticFeedback('light');
    setActiveTab(tab);
  };

  // Enhanced bet placement with real smart contract
  const handlePlaceBet = async () => {
    console.log(`ðŸ”¥ BET BUTTON CLICKED - Amount: ${betAmount} TON`);

    if (betAmount <= 0) {
      showAlert('Please enter a valid bet amount');
      return;
    }

    if (!isConnected) {
      showAlert('Please connect your wallet first');
      return;
    }

    if (!contractState.isActive) {
      showAlert('Jackpot is not currently active\n\nðŸ¤– Automated system will start a new round soon...');
      return;
    }

    try {
      console.log(`ðŸš€ Starting bet placement process...`);
      hapticFeedback('medium');

      // This will call the contract with opcode 0x03
      const result = await placeBet(betAmount);

      if (result.success) {
        hapticFeedback('success');
        showAlert(`âœ… Bet placed successfully!\n${betAmount} TON in round #${currentRound}\nTransaction: ${result.txHash.slice(0, 8)}...`);
        console.log('ðŸŽ‰ Bet placement completed:', result);
      }
    } catch (error) {
      hapticFeedback('error');
      showAlert(`âŒ Bet failed: ${error.message}`);
      console.error('ðŸ’¥ Bet error:', error);
    }
  };

  return (
    <div className="app">
      {/* Header */}
      <header className="header">
        <div className="header-container">
          <div className="header-left">
            <div className="logo">
              <div className="logo-icon">
                <div className="logo-circle"></div>
              </div>
              <span className="logo-text">SLOTPOT</span>
            </div>

            <nav className="nav-tabs">
              <div className="nav-tab active">
                <Trophy size={20} />
                <span>Jackpot</span>
              </div>
              <div className="nav-tab">
                <DollarSign size={20} />
                <span>Coinflip</span>
              </div>
              <div className="nav-tab">
                <Users size={20} />
                <span>Affiliates</span>
              </div>
            </nav>
          </div>

          <div className="header-right">
            <div className="user-info">
              {user && (
                <span className="user-greeting">
                  {hasRealUserData ? 'ðŸ‘‹' : 'ðŸ¤–'} {user.displayName}
                </span>
              )}
              {isConnected && (
                <div className="wallet-info">
                  <span className="wallet-address">{address}</span>
                </div>
              )}
            </div>
            <WalletConnection />
          </div>
        </div>
      </header>

      <div className="app-container">
        {/* Mobile Tab Navigation */}
        <div className="mobile-tab-nav">
          <button 
            className={`mobile-tab ${activeTab === 'jackpot' ? 'active' : ''}`}
            onClick={() => handleTabChange('jackpot')}
          >
            <Trophy size={16} />
            Jackpot
          </button>
          <button 
            className={`mobile-tab ${activeTab === 'chat' ? 'active' : ''}`}
            onClick={() => handleTabChange('chat')}
          >
            <MessageCircle size={16} />
            Chat
          </button>
        </div>

        {/* Content Sections */}
        <div className={`mobile-content-section ${activeTab === 'jackpot' ? 'active' : ''}`}>
          <div className="main-content">
            <GameArea 
              jackpotValue={jackpotValue}
              currentRound={currentRound}
              timeRemaining={timeRemaining}
              isLive={isLive}
              showWinnerAnnouncement={showWinnerAnnouncement}
              betAmount={betAmount}
              onBetChange={handleBetChange}
              onPlaceBet={handlePlaceBet}
              isConnected={isConnected}
              isLoading={isPlacingBet}
              userWinChance={userWinChance}
              userBetTotal={userBetTotal}
              contractState={{
                ...contractState,
                userAddress: address // Add user address for bet tracking
              }}
              isLoadingContract={isLoadingContract}
              adminInfo={adminInfo}
              currentBettors={gameBettors}
              // Winner state props (moved from GameArea internal state)
              waitingForWinner={waitingForWinner}
              contractWinner={contractWinner}
              postWinnerLoading={postWinnerLoading}
            />
          </div>
        </div>

        <div className={`mobile-content-section ${activeTab === 'chat' ? 'active' : ''}`}>
          <div className="mobile-chat-section">
            <MobileChat />
          </div>
        </div>

        {/* Hidden desktop sidebars */}
        <div className="sidebar-left">
          <Chat />
        </div>
      </div>

      {/* Mobile Footer */}
      <div className="mobile-footer">
        <div className="mobile-footer-content">
          <div className="mobile-stats">
            <div className="mobile-stat">
              <div className="mobile-stat-value">{contractState.isActive !== false ? (contractState.betCount || 0) : 0}</div>
              <div className="mobile-stat-label">Players</div>
            </div>
            <div className="mobile-stat">
              <div className="mobile-stat-value">#{currentRound}</div>
              <div className="mobile-stat-label">Round</div>
            </div>
            {isConnected && contractState.isActive !== false && userBetTotal > 0 && (
              <div className="mobile-stat">
                <div className="mobile-stat-value">{userWinChance.toFixed(1)}%</div>
                <div className="mobile-stat-label">Win Chance</div>
              </div>
            )}
          </div>
          <button className="mobile-games-btn">
            <Users size={14} />
            Games
          </button>
        </div>
      </div>

      {/* Contract Status Indicator */}
      {isLoadingContract && (
        <div className="loading-overlay">
          <div className="loading-spinner">ðŸ”„</div>
          <span>Loading contract data...</span>
        </div>
      )}
    </div>
  );
}

// Main App component with TON Connect Provider
function App() {
  return (
    <TonConnectUIProvider 
      manifestUrl={manifestUrl}
      uiPreferences={uiOptions.uiPreferences}
      language={uiOptions.language}
      restoreConnection={uiOptions.restoreConnection}
      actionsConfiguration={uiOptions.actionsConfiguration}
    >
      <AppContent />
    </TonConnectUIProvider>
  );
}

export default App;
