import React, { useRef, useEffect, useState, useCallback } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay } from 'swiper/modules';
import 'swiper/css';
import './SimpleCarousel.css';

const SimpleCarousel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const swiperRef = useRef(null);
  const [isCustomSpinning, setIsCustomSpinning] = useState(false);
  const [spinSpeed, setSpinSpeed] = useState(50);
  const [showingWinner, setShowingWinner] = useState(false);
  const [lockedPosition, setLockedPosition] = useState(null);
  const animationRef = useRef(null);
  const targetIndexRef = useRef(null);
  const spinPhaseRef = useRef('idle'); // 'idle', 'spinning', 'slowing', 'locked'
  const winnerTimeoutRef = useRef(null);

  // Handle swiper initialization
  const handleSwiperInit = (swiper) => {
    swiperRef.current = swiper;
  };

  // COMPLETELY LOCK CAROUSEL AT SPECIFIC POSITION
  const lockCarouselAtPosition = useCallback((targetIndex) => {
    if (!swiperRef.current) return;

    console.log('üîí LOCKING carousel at winner position:', targetIndex);

    // Calculate the exact position for the winner to be centered
    const slideWidth = 120; // Width of each slide + gap
    const containerWidth = swiperRef.current.el.clientWidth;
    const centerOffset = containerWidth / 2 - slideWidth / 2;

    // Calculate position to center the winner slide
    const targetPosition = -(targetIndex * slideWidth) + centerOffset;

    // FORCE STOP ALL SWIPER FUNCTIONALITY
    if (swiperRef.current.autoplay) {
      swiperRef.current.autoplay.stop();
      swiperRef.current.autoplay.pause();
      swiperRef.current.autoplay.running = false;
    }

    // Disable all Swiper interactions
    swiperRef.current.allowSlideNext = false;
    swiperRef.current.allowSlidePrev = false;
    swiperRef.current.allowTouchMove = false;

    // Get the wrapper element and FORCE position
    const wrapper = swiperRef.current.wrapperEl;
    if (wrapper) {
      // Remove all CSS classes that might interfere
      wrapper.style.transition = 'none';
      wrapper.style.transitionDuration = '0ms';
      wrapper.style.transitionDelay = '0ms';
      wrapper.style.animation = 'none';

      // FORCE the exact position
      wrapper.style.transform = `translate3d(${targetPosition}px, 0px, 0px)`;

      // Lock it in place by setting position as important
      wrapper.style.setProperty('transform', `translate3d(${targetPosition}px, 0px, 0px)`, 'important');
      wrapper.style.setProperty('transition', 'none', 'important');
    }

    // Disable all slide transitions
    const slides = swiperRef.current.slides;
    if (slides) {
      slides.forEach(slide => {
        slide.style.transition = 'none';
        slide.style.transitionDuration = '0ms';
      });
    }

    // Set locked state
    setLockedPosition(targetPosition);
    setShowingWinner(true);
    spinPhaseRef.current = 'locked';

    console.log('üèÜ Carousel LOCKED at position:', targetPosition);

    // Trigger winner callback
    setTimeout(() => {
      onWinnerLand?.();
    }, 500);

    // Keep locked for 8 seconds
    winnerTimeoutRef.current = setTimeout(() => {
      unlockCarousel();
    }, 8000);

  }, [onWinnerLand]);

  // UNLOCK CAROUSEL AND RESUME NORMAL OPERATION
  const unlockCarousel = useCallback(() => {
    if (!swiperRef.current) return;

    console.log('üîì UNLOCKING carousel - resuming normal operation');

    // Re-enable Swiper functionality
    swiperRef.current.allowSlideNext = true;
    swiperRef.current.allowSlidePrev = true;
    swiperRef.current.allowTouchMove = false; // Keep touch disabled

    // Reset wrapper styles
    const wrapper = swiperRef.current.wrapperEl;
    if (wrapper) {
      wrapper.style.transition = '';
      wrapper.style.transitionDuration = '';
      wrapper.style.transitionDelay = '';
      wrapper.style.animation = '';
      wrapper.style.removeProperty('transform');
    }

    // Reset slide styles
    const slides = swiperRef.current.slides;
    if (slides) {
      slides.forEach(slide => {
        slide.style.transition = '';
        slide.style.transitionDuration = '';
      });
    }

    // Reset states
    setLockedPosition(null);
    setShowingWinner(false);
    spinPhaseRef.current = 'idle';

    // Resume autoplay if no winner
    if (!winner && swiperRef.current.autoplay) {
      swiperRef.current.autoplay.running = true;
      swiperRef.current.autoplay.start();
    }
  }, [winner]);

  // Natural casino-style spin animation
  const startCasinoSpin = useCallback(() => {
    if (!swiperRef.current || isCustomSpinning) return;

    console.log('üé∞ Starting casino-style spin animation');
    setIsCustomSpinning(true);
    setShowingWinner(false);
    setLockedPosition(null);
    spinPhaseRef.current = 'spinning';

    // Stop any existing autoplay
    if (swiperRef.current.autoplay) {
      swiperRef.current.autoplay.stop();
    }

    // Start fast spinning
    let currentSpeed = 20; // Start very fast
    setSpinSpeed(currentSpeed);

    const spinInterval = setInterval(() => {
      if (swiperRef.current && spinPhaseRef.current === 'spinning') {
        // Move to next slide
        swiperRef.current.slideNext(currentSpeed);
      }
    }, currentSpeed);

    // Save interval reference for cleanup
    animationRef.current = spinInterval;

    return spinInterval;
  }, [isCustomSpinning]);

  // Natural deceleration to winner - ends with LOCK instead of slideTo
  const startSlowDownToWinner = useCallback((winnerIndex) => {
    if (!swiperRef.current || spinPhaseRef.current !== 'spinning') return;

    console.log('üéØ Starting slow-down to winner at index:', winnerIndex);
    spinPhaseRef.current = 'slowing';

    // Clear fast spinning
    if (animationRef.current) {
      clearInterval(animationRef.current);
    }

    // Start deceleration animation
    let speed = 50; // Start medium speed
    let slideCount = 0;
    const maxSlides = 20; // Maximum slides before forcing stop

    const decelerateInterval = setInterval(() => {
      if (!swiperRef.current) {
        clearInterval(decelerateInterval);
        return;
      }

      slideCount++;

      // Move to next slide
      swiperRef.current.slideNext(speed);

      // Check if we should stop (after enough deceleration)
      if (slideCount >= maxSlides || speed > 1000) {
        clearInterval(decelerateInterval);

        // LOCK at winner position instead of using slideTo
        setTimeout(() => {
          setIsCustomSpinning(false);
          lockCarouselAtPosition(winnerIndex);
        }, 500);
      } else {
        // Gradually slow down
        speed = Math.min(speed * 1.15, 1200); // Faster deceleration
        setSpinSpeed(speed);
      }
    }, speed);

    animationRef.current = decelerateInterval;
  }, [lockCarouselAtPosition]);

  // Handle winner announcement with realistic spin
  useEffect(() => {
    if (winner && swiperRef.current && players && !isCustomSpinning && !showingWinner) {
      const winnerIndex = players.findIndex(p => 
        p && (
          p.address === winner.address || 
          p.username === winner.username ||
          p.address === winner.winner ||
          p.address === winner.fullAddress ||
          p.username === winner.displayName
        )
      );

      if (winnerIndex !== -1) {
        targetIndexRef.current = winnerIndex;
        console.log('üèÜ Winner found at index:', winnerIndex, 'Starting casino spin...');

        // Clear any existing winner timeout
        if (winnerTimeoutRef.current) {
          clearTimeout(winnerTimeoutRef.current);
        }

        // Start spinning, then slow down to winner
        startCasinoSpin();

        setTimeout(() => {
          startSlowDownToWinner(winnerIndex);
        }, 2500); // Spin for 2.5 seconds before starting to slow down
      }
    }
  }, [winner, players, isCustomSpinning, showingWinner, startCasinoSpin, startSlowDownToWinner]);

  // Handle regular spinning state (not winner selection)
  useEffect(() => {
    // Don't do anything if locked or showing winner
    if (showingWinner || spinPhaseRef.current === 'locked') {
      return;
    }

    if (isSpinning && !winner && !isCustomSpinning && !showingWinner) {
      // Regular fast spinning while waiting for winner
      if (swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.start();
      }
    } else if (!isSpinning && !winner && !isCustomSpinning && !showingWinner && spinPhaseRef.current === 'idle') {
      // Normal slow movement when not spinning and not showing winner
      if (swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.start();
      }
    }
  }, [isSpinning, winner, isCustomSpinning, showingWinner]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (animationRef.current) {
        clearInterval(animationRef.current);
      }
      if (winnerTimeoutRef.current) {
        clearTimeout(winnerTimeoutRef.current);
      }
    };
  }, []);

  // Prevent autoplay restart during locked state
  useEffect(() => {
    if (showingWinner || spinPhaseRef.current === 'locked') {
      // Force stop during locked state
      if (swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.stop();
        swiperRef.current.autoplay.running = false;
      }
      return;
    }

    // Only restart normal autoplay if we're in idle state
    if (!winner && !isCustomSpinning && spinPhaseRef.current === 'idle' && swiperRef.current && swiperRef.current.autoplay) {
      swiperRef.current.autoplay.running = true;
      swiperRef.current.autoplay.start();
    }
  }, [players.length, isCustomSpinning, showingWinner, winner]);

  // Reset states when winner prop is cleared (new round)
  useEffect(() => {
    if (!winner && showingWinner) {
      console.log('üîÑ Winner cleared - resetting carousel states');
      unlockCarousel();
    }
  }, [winner, showingWinner, unlockCarousel]);

  return (
    <div className={`simple-carousel-container ${showingWinner ? 'winner-showing' : ''}`}>
      {/* Target Arrow */}
      <div className={`carousel-arrow ${
        isCustomSpinning && spinPhaseRef.current === 'slowing' ? 'winner-approaching' : 
        spinPhaseRef.current === 'locked' || showingWinner ? 'winner-landed' : ''
      }`}>‚¨áÔ∏è</div>

      <Swiper
        ref={swiperRef}
        modules={[Autoplay]}
        spaceBetween={10}
        slidesPerView="auto"
        centeredSlides={true}
        loop={true}
        loopedSlides={players.length * 6}
        autoplay={showingWinner || spinPhaseRef.current === 'locked' ? false : {
          delay: isSpinning && !isCustomSpinning && !showingWinner ? 15 : 400,
          disableOnInteraction: false,
          pauseOnMouseEnter: false,
        }}
        speed={isCustomSpinning ? spinSpeed : (isSpinning && !showingWinner ? 30 : 300)}
        className={`player-swiper ${(isSpinning || isCustomSpinning) && !showingWinner ? 'spinning' : ''} ${showingWinner ? 'winner-display' : ''}`}
        allowTouchMove={false}
        onSwiper={handleSwiperInit}
      >
        {/* Create 6 sets for much smoother infinite scroll */}
        {[...Array(6)].map((_, setIndex) => 
          players.map((player, index) => (
            <SwiperSlide key={`${setIndex}-${player?.address || `waiting-${index}`}`} className="player-slide">
              <div className={`player-card ${
                winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) ? 'winner' : player ? 'active' : 'waiting'
              }`}>
                <div className="player-avatar">
                  {player ? (
                    <div className="player-avatar-content">
                      {player.username ? player.username[0].toUpperCase() : '?'}
                    </div>
                  ) : (
                    <div className="waiting-icon">‚ùì</div>
                  )}
                </div>
                <div className="player-info">
                  <div className="player-name">
                    {player ? (player.username || player.displayName || 'Player') : 'Waiting'}
                  </div>
                  <div className="player-bet">
                    {player ? `${(player.amount || 0).toFixed(2)} TON` : '0.00 TON'}
                  </div>
                  {winner && player && (
                    player.address === winner.address || 
                    player.username === winner.username ||
                    player.address === winner.winner ||
                    player.address === winner.fullAddress ||
                    player.username === winner.displayName
                  ) && (
                    <div className="winner-prize">
                      Won {parseFloat(winner.prize || 0).toFixed(3)} TON
                    </div>
                  )}
                </div>
                {winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) && (
                  <div className="winner-crown">üëë</div>
                )}
              </div>
            </SwiperSlide>
          ))
        )}
      </Swiper>
    </div>
  );
};

export default SimpleCarousel;
