import React, { useRef, useEffect, useState, useCallback } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay } from 'swiper/modules';
import 'swiper/css';
import './SimpleCarousel.css';

const SimpleCarousel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const swiperRef = useRef(null);
  const [winnerAnimationActive, setWinnerAnimationActive] = useState(false);
  const [winnerLocked, setWinnerLocked] = useState(false);
  const [showWinnerStyling, setShowWinnerStyling] = useState(false);
  const winnerTimeoutRef = useRef(null);
  const animationAttemptsRef = useRef(0);
  const lastWinnerRef = useRef(null);

  // Handle swiper initialization
  const handleSwiperInit = (swiper) => {
    swiperRef.current = swiper;
  };

  // Simple winner animation - much more reliable
  const animateToWinner = useCallback(() => {
    if (!swiperRef.current || !winner || winnerAnimationActive) {
      console.log('ğŸš« Animation blocked:', { 
        hasSwiper: !!swiperRef.current, 
        hasWinner: !!winner, 
        isActive: winnerAnimationActive 
      });
      return;
    }

    console.log('ğŸ° Starting SIMPLE winner animation');
    console.log('ğŸ” Winner data:', winner);

    setWinnerAnimationActive(true);

    // Stop current autoplay immediately
    if (swiperRef.current.autoplay) {
      swiperRef.current.autoplay.stop();
    }

    // STEP 1: SIMPLE - Find winner in players array (no DOM searching needed!)
    console.log('ğŸ” Finding winner in players array...');
    console.log('ğŸ” Winner data:', { username: winner?.username, address: winner?.address });
    console.log('ğŸ” Players array:', players.map((p, i) => ({ index: i, username: p?.username, address: p?.address })));

    // Find winner position in players array directly
    let realIndex = players.findIndex(player => {
      if (!player) return false;

      // Simple matching - check address first (most reliable), then username
      return player.address === winner.address || 
             player.address === winner.winner || 
             player.username === winner.username ||
             player.username === winner.displayName;
    });

    if (realIndex === -1) {
      console.warn('âš ï¸ Winner not found in players array - using first player as fallback');
      realIndex = players.findIndex(player => player !== null);

      if (realIndex === -1) {
        console.error('ğŸ’¥ No players found at all!');
        setWinnerAnimationActive(false);
        setShowWinnerStyling(true);
        onWinnerLand?.();
        return;
      }
    }

    console.log('âœ… Winner found at players array index:', realIndex);
    console.log('ğŸ¯ Target position for animation:', realIndex);

    // STEP 2: Simple but effective slot machine animation
    console.log('ğŸ° Starting slot machine animation...');

    let moveCount = 0;
    const maxMoves = 10; // Fixed number for consistency
    let isLanding = false; // Prevent multiple landings

    const moveToNext = () => {
      if (!swiperRef.current || isLanding) {
        console.log('ğŸš« Animation stopped or already landing');
        return;
      }

      moveCount++;
      console.log(`ğŸ° Move ${moveCount}/${maxMoves}`);

      // Calculate speed - gets slower over time
      let speed = 150;
      let delay = 200;

      if (moveCount > 7) {
        // Very slow in final moves
        speed = 400 + (moveCount * 100);
        delay = 500 + (moveCount * 150);
        console.log(`ğŸ¯ Final moves - very slow (${speed}ms)`);
      } else if (moveCount > 4) {
        // Medium slow in middle
        speed = 250 + (moveCount * 50);
        delay = 300 + (moveCount * 50);
        console.log(`ğŸ° Slowing down (${speed}ms)`);
      }

      // Move one step
      swiperRef.current.slideNext(speed);

      // Check if we should land
      if (moveCount >= maxMoves) {
        console.log('ğŸ† Time to land on winner!');
        isLanding = true;

        // NEW SIMPLE SYSTEM: Land on winner naturally without rollback
        setTimeout(() => {
          console.log('ğŸ¯ Landing on winner slide...');

          // Get current position
          const currentActive = swiperRef.current.activeIndex;
          const slidesPerSet = players.length;

          console.log(`ğŸ¯ Current position: ${currentActive}, Target real index: ${realIndex}`);

          // Calculate how many steps forward to reach the winner position
          // We want to land on realIndex position within the current cycle
          let stepsToWinner = 0;
          let currentRealIndex = currentActive % slidesPerSet;

          // Calculate forward distance to winner
          if (realIndex >= currentRealIndex) {
            stepsToWinner = realIndex - currentRealIndex;
          } else {
            // Wrap around (go to next cycle)
            stepsToWinner = (slidesPerSet - currentRealIndex) + realIndex;
          }

          // If steps is too far, find closer path in loop
          if (stepsToWinner > slidesPerSet / 2) {
            // Go backwards instead (previous cycle)
            stepsToWinner = stepsToWinner - slidesPerSet;
          }

          console.log(`ğŸ¯ Steps to winner: ${stepsToWinner} (from real ${currentRealIndex} to ${realIndex})`);

          // Move step by step to winner for smooth natural landing
          let currentStep = 0;
          const landingSteps = Math.abs(stepsToWinner);

                     const stepToWinner = () => {
             if (!swiperRef.current || currentStep >= landingSteps) {
               // Reached winner - IMMEDIATE HARD LOCK
               console.log('ğŸ† Landed on winner - HARD LOCKING!');

               if (swiperRef.current) {
                 // IMMEDIATELY stop everything
                 swiperRef.current.autoplay?.stop();
                 swiperRef.current.allowSlideNext = false;
                 swiperRef.current.allowSlidePrev = false;
                 swiperRef.current.allowTouchMove = false;

                 // FORCE stop all transitions immediately
                 const wrapper = swiperRef.current.wrapperEl;
                 if (wrapper) {
                   wrapper.style.transition = 'none !important';
                   wrapper.style.transform = getComputedStyle(wrapper).transform;

                   // Double-check - force stop any ongoing transitions
                   setTimeout(() => {
                     wrapper.style.transition = 'none';
                     wrapper.style.transform = getComputedStyle(wrapper).transform;
                   }, 50);
                 }

                 // Disable any swiper animations
                 swiperRef.current.params.speed = 0;

                 // Force destroy any pending animations
                 if (swiperRef.current.animating) {
                   swiperRef.current.animating = false;
                 }
               }

               setWinnerLocked(true);
               setWinnerAnimationActive(false);

               // NOW show the winner styling (green highlight + crown + prize)
               console.log('ğŸ‰ NOW showing winner in green!');
               setShowWinnerStyling(true);

               onWinnerLand?.();

               console.log('âœ… Winner animation complete and LOCKED!');
               return;
             }

            currentStep++;
            console.log(`ğŸ¯ Landing step ${currentStep}/${landingSteps}`);

            // Move one step toward winner
            if (stepsToWinner > 0) {
              swiperRef.current.slideNext(400);
            } else if (stepsToWinner < 0) {
              swiperRef.current.slidePrev(400);
            }

            // Continue to next step
            setTimeout(() => {
              stepToWinner();
            }, 450);
          };

          // Start step-by-step landing
          if (landingSteps > 0) {
            stepToWinner();
          } else {
            // Already on winner position
            console.log('ğŸ† Already on winner position - locking!');

            if (swiperRef.current) {
              swiperRef.current.autoplay.stop();
              swiperRef.current.allowSlideNext = false;
              swiperRef.current.allowSlidePrev = false;
              swiperRef.current.allowTouchMove = false;

              const wrapper = swiperRef.current.wrapperEl;
              if (wrapper) {
                wrapper.style.transition = 'none';
              }
            }

            setWinnerLocked(true);
            setWinnerAnimationActive(false);
            setShowWinnerStyling(true);
            onWinnerLand?.();

            console.log('âœ… Winner animation complete!');
          }

        }, delay);

        return;
      }

      // Continue moving
      setTimeout(() => {
        moveToNext();
      }, delay);
    };



    // Start animation
    setTimeout(() => {
      moveToNext();
    }, 300);
  }, [winner, winnerAnimationActive, onWinnerLand]);

  // Lock carousel on winner card
  const lockOnWinner = useCallback(() => {
    if (!swiperRef.current || !winner) return;

    console.log('ğŸ”’ Locking on winner...');

    // Find winner slide by looking at visible slides
    const slides = swiperRef.current.slides;
    let winnerSlideIndex = -1;

    // Get winner info for matching
    const winnerUsername = winner?.username || winner?.displayName;
    const winnerAddress = winner?.address || winner?.winner || winner?.fullAddress;

    console.log('ğŸ” Looking for winner:', { winnerUsername, winnerAddress });

    // Find the slide with winner content that's closest to center
    let closestToCenter = Infinity;
    const containerCenter = swiperRef.current.el.clientWidth / 2;

    for (let i = 0; i < slides.length; i++) {
      const slide = slides[i];
      const playerNameEl = slide.querySelector('.player-name');
      const playerName = playerNameEl?.textContent?.trim();

      console.log(`ğŸ” Checking slide ${i}: "${playerName}"`);

      // Check if this is the winner - improved matching logic
      let isWinner = false;

      if (winnerUsername && playerName) {
        // Direct username match
        isWinner = playerName === winnerUsername;

        // Fallback: check if displayName matches
        if (!isWinner) {
          isWinner = playerName === (winner?.displayName);
        }
      }

      if (!isWinner && winnerAddress && playerName) {
        // Address-based matching (for cases where username is an address)
        isWinner = playerName.includes(winnerAddress.slice(0, 6)) || 
                   winnerAddress.includes(playerName);
      }

      // Special case: if winner.displayName is 'Player' and we have a player named 'Player'
      if (!isWinner && (winnerUsername === 'Player' || winner?.displayName === 'Player')) {
        isWinner = playerName === 'Player';
      }

      console.log(`ğŸ” Slide ${i} "${playerName}" isWinner: ${isWinner}`);

      if (isWinner) {
        // Calculate distance from center
        const slideRect = slide.getBoundingClientRect();
        const containerRect = swiperRef.current.el.getBoundingClientRect();
        const slideCenter = slideRect.left - containerRect.left + slideRect.width / 2;
        const distanceFromCenter = Math.abs(slideCenter - containerCenter);

        console.log(`âœ… Found winner candidate at slide ${i}, distance from center: ${distanceFromCenter}`);

        if (distanceFromCenter < closestToCenter) {
          closestToCenter = distanceFromCenter;
          winnerSlideIndex = i;
        }
      }
    }

    if (winnerSlideIndex !== -1) {
      console.log('âœ… Found winner slide at index:', winnerSlideIndex);

      // Slide to winner smoothly
      swiperRef.current.slideTo(winnerSlideIndex, 1000, false);

      // Lock after slide completes
      setTimeout(() => {
        console.log('ğŸ† Winner locked in position!');

        // Completely stop all movement
        if (swiperRef.current) {
          swiperRef.current.autoplay.stop();
          swiperRef.current.allowSlideNext = false;
          swiperRef.current.allowSlidePrev = false;

          // Force stop transitions
          const wrapper = swiperRef.current.wrapperEl;
          if (wrapper) {
            setTimeout(() => {
              wrapper.style.transition = 'none';
              const transform = getComputedStyle(wrapper).transform;
              wrapper.style.transform = transform;
            }, 100);
          }
        }

        setWinnerLocked(true);
        onWinnerLand?.();

        // Show winner for 7 seconds
        winnerTimeoutRef.current = setTimeout(() => {
          resetToIdle();
        }, 7000);

      }, 1100);
    } else {
      console.warn('âŒ Could not find winner slide');
      console.log('ğŸ” Available slides:', slides.length);
      console.log('ğŸ” Winner data:', winner);

      // Improved fallback - try to find any slide with winner class
      let fallbackIndex = -1;
      for (let i = 0; i < slides.length; i++) {
        const slide = slides[i];
        if (slide.querySelector('.player-card.winner')) {
          fallbackIndex = i;
          console.log(`ğŸ¯ Found fallback winner slide at index ${i}`);
          break;
        }
      }

      if (fallbackIndex !== -1) {
        console.log('ğŸ¯ Using fallback winner slide at index:', fallbackIndex);
        swiperRef.current.slideTo(fallbackIndex, 1000, false);

        setTimeout(() => {
          setWinnerLocked(true);
          onWinnerLand?.();

          winnerTimeoutRef.current = setTimeout(() => {
            resetToIdle();
          }, 7000);
        }, 1100);
      } else {
        // Last resort - just stop and show winner display
        console.log('ğŸš¨ Fallback: Just stopping and showing winner');
        setWinnerLocked(true);
        onWinnerLand?.();

        winnerTimeoutRef.current = setTimeout(() => {
          resetToIdle();
        }, 7000);
      }
    }
  }, [winner, onWinnerLand]);

  // Reset to normal idle state
  const resetToIdle = useCallback(() => {
    console.log('ğŸ”„ Resetting to idle state');

    if (swiperRef.current) {
      // Re-enable all swiper functionality
      swiperRef.current.allowSlideNext = true;
      swiperRef.current.allowSlidePrev = true;
      swiperRef.current.allowTouchMove = false; // Keep disabled for this app

      // Reset wrapper transitions
      const wrapper = swiperRef.current.wrapperEl;
      if (wrapper) {
        wrapper.style.transition = '';
        wrapper.style.transform = '';
      }

      // Start normal autoplay if no active states
      if (!isSpinning && !winner) {
        setTimeout(() => {
          if (swiperRef.current && swiperRef.current.autoplay) {
            swiperRef.current.autoplay.start();
          }
        }, 500);
      }
    }

    setWinnerAnimationActive(false);
    setWinnerLocked(false);
    setShowWinnerStyling(false); // Reset winner styling

    // Reset animation attempts when winner is cleared
    if (!winner) {
      animationAttemptsRef.current = 0;
      lastWinnerRef.current = null;
    }

    if (winnerTimeoutRef.current) {
      clearTimeout(winnerTimeoutRef.current);
      winnerTimeoutRef.current = null;
    }
  }, [isSpinning, winner]);

  // Start winner animation when winner is announced
  useEffect(() => {
    // Strong guards to prevent multiple animations
    if (!winner) {
      console.log('ğŸš« No winner, skipping animation');
      setShowWinnerStyling(false); // Hide winner styling when no winner
      return;
    }

    if (winnerAnimationActive) {
      console.log('ğŸš« Animation already active, skipping');
      return;
    }

    if (winnerLocked) {
      console.log('ğŸš« Winner already locked, skipping');
      return;
    }

    // Reset winner styling for new animation
    setShowWinnerStyling(false);
    console.log('ğŸ° Starting animation - winner styling hidden until animation completes');

    // Check if this is the same winner we already tried
    const currentWinnerKey = `${winner.username || winner.displayName || 'Player'}-${winner.address || winner.winner || ''}`;
    const lastWinnerKey = lastWinnerRef.current;

    // Reset attempts if this is a new winner
    if (currentWinnerKey !== lastWinnerKey) {
      console.log('ğŸ†• New winner detected, resetting attempts');
      animationAttemptsRef.current = 0;
      lastWinnerRef.current = currentWinnerKey;
    }

    // Prevent infinite loops - max 1 attempt (was 3)
    if (animationAttemptsRef.current >= 1) {
      console.warn('ğŸš« Max animation attempts reached for winner:', winner.username || winner.displayName || 'Player');
      console.log('ğŸ¯ Forcing winner display without animation');

      // Just show the winner without animation
      setWinnerLocked(true);
      setShowWinnerStyling(true); // Show styling immediately if no animation
      onWinnerLand?.();

      // NO AUTO-RESET - let game logic handle it
      console.log('ğŸ¯ Winner locked without animation - waiting for new round');

      return;
    }

    console.log('ğŸ† Winner announced:', winner.username || winner.displayName || 'Player');
    console.log(`ğŸ”„ Animation attempt ${animationAttemptsRef.current + 1}/1`);

    animationAttemptsRef.current++;

    // Small delay to ensure DOM is ready
    setTimeout(() => {
      animateToWinner();
    }, 100);

  }, [winner, winnerAnimationActive, winnerLocked, animateToWinner, onWinnerLand]);

  // Handle normal spinning (analyzing bets)
  useEffect(() => {
    if (!swiperRef.current) return;

    if (isSpinning && !winner && !winnerAnimationActive) {
      // Fast autoplay during analysis
      swiperRef.current.params.autoplay = {
        delay: 20,
        disableOnInteraction: false,
      };
      swiperRef.current.autoplay.start();
    } else if (!isSpinning && !winner && !winnerAnimationActive && !winnerLocked) {
      // Normal idle autoplay
      swiperRef.current.params.autoplay = {
        delay: 400,
        disableOnInteraction: false,
      };
      swiperRef.current.autoplay.start();
    }
  }, [isSpinning, winner, winnerAnimationActive, winnerLocked]);

  // Reset when winner is cleared (new round)
  useEffect(() => {
    if (!winner && (winnerAnimationActive || winnerLocked)) {
      console.log('ğŸ”„ Winner cleared - resetting');
      resetToIdle();
    }
  }, [winner, winnerAnimationActive, winnerLocked, resetToIdle]);

  // Cleanup
  useEffect(() => {
    return () => {
      if (winnerTimeoutRef.current) {
        clearTimeout(winnerTimeoutRef.current);
      }
    };
  }, []);

  return (
    <div className="simple-carousel-container">
      {/* Target Arrow */}
      <div className={`carousel-arrow ${
        winnerAnimationActive && !winnerLocked ? 'winner-approaching' : 
        winnerLocked ? 'winner-landed' : ''
      }`}>â¬‡ï¸</div>

      <Swiper
        ref={swiperRef}
        modules={[Autoplay]}
        spaceBetween={10}
        slidesPerView="auto"
        centeredSlides={true}
        loop={true}
        loopedSlides={Math.max(players.length, 6)}
        autoplay={{
          delay: isSpinning && !winnerAnimationActive ? 20 : 400,
          disableOnInteraction: false,
          pauseOnMouseEnter: false,
        }}
        speed={300}
        className={`player-swiper ${isSpinning || winnerAnimationActive ? 'spinning' : ''}`}
        allowTouchMove={false}
        onSwiper={handleSwiperInit}
      >
        {/* Only 2 sets to reduce complexity */}
        {[...Array(2)].map((_, setIndex) => 
          players.map((player, index) => (
            <SwiperSlide key={`${setIndex}-${player?.address || `waiting-${index}`}`} className="player-slide">
              <div className={`player-card ${
                showWinnerStyling && winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) ? 'winner' : player ? 'active' : 'waiting'
              }`}>
                <div className="player-avatar">
                  {player ? (
                    <div className="player-avatar-content">
                      {player.username ? player.username[0].toUpperCase() : '?'}
                    </div>
                  ) : (
                    <div className="waiting-icon">â“</div>
                  )}
                </div>
                <div className="player-info">
                  <div className="player-name">
                    {player ? (player.username || player.displayName || 'Player') : 'Waiting'}
                  </div>
                  <div className="player-bet">
                    {player ? `${(player.amount || 0).toFixed(2)} TON` : '0.00 TON'}
                  </div>
                  {showWinnerStyling && winner && player && (
                    player.address === winner.address || 
                    player.username === winner.username ||
                    player.address === winner.winner ||
                    player.address === winner.fullAddress ||
                    player.username === winner.displayName
                  ) && (
                    <div className="winner-prize">
                      Won {parseFloat(winner.prize || 0).toFixed(3)} TON
                    </div>
                  )}
                </div>
                {showWinnerStyling && winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) && (
                  <div className="winner-crown">ğŸ‘‘</div>
                )}
              </div>
            </SwiperSlide>
          ))
        )}
      </Swiper>
    </div>
  );
};

export default SimpleCarousel;
