import React, { useState, useEffect, useRef, useCallback } from 'react';
import { TonIcon } from './IconComponents';

const SimpleCarousel = ({ 
  players, 
  isSpinning, 
  onSpinComplete, 
  contractWinner,
  totalJackpotValue 
}) => {
  const [isAnimating, setIsAnimating] = useState(false);
  const [isIdleMoving, setIsIdleMoving] = useState(true);
  const wheelRef = useRef();
  const idleAnimationRef = useRef();

  // Card dimensions (matching roulette example)
  const CARD_WIDTH = 81; // 75px + 3px margin on each side

  // Render individual player card HTML
  const renderPlayerCard = useCallback((player, key) => {
    if (!player) return '<div class="waiting-card">Waiting...</div>';

    const isActive = player.isRealPlayer && player.bet > 0;
    const avatar = player.avatar && player.avatar !== '❓' ? player.avatar : "/img/unknown.webp";
    const username = player.username || player.displayName || player.name || 'Player';
    const bet = isActive ? (player.bet?.toFixed(3) || '0.000') : '0.000';

    return `
      <div class="player-card ${isActive ? 'active' : 'inactive'}" data-key="${key}">
        <div class="card-content">
          <div class="avatar-container">
            <img src="${avatar}" alt="${username}" class="player-avatar" />
          </div>
          <div class="player-info">
            <p class="player-name">${username}</p>
            <div class="bet-info">
              <img src="${isActive ? 'https://raw.githubusercontent.com/solana-labs/token-list/main/assets/mainnet/So11111111111111111111111111111111111111112/logo.png' : '/img/solana.png'}" 
                   class="coin-icon ${!isActive ? 'grayscale' : ''}" alt="" />
              <span class="bet-amount">${bet}</span>
            </div>
          </div>
        </div>
      </div>
    `;
  }, []);

  // Initialize wheel with player rows
  const initWheel = useCallback(() => {
    if (!wheelRef.current || !players.length) return;

    const wheel = wheelRef.current;
    wheel.innerHTML = ''; // Clear existing content

    // Create multiple rows like in roulette example (29 rows)
    for (let rowIndex = 0; rowIndex < 29; rowIndex++) {
      const row = document.createElement('div');
      row.className = 'roulette-row';

      // Add all players to each row
      players.forEach((player, playerIndex) => {
        const card = document.createElement('div');
        card.className = 'roulette-card';
        card.innerHTML = renderPlayerCard(player, `${rowIndex}-${playerIndex}`);
        row.appendChild(card);
      });

      wheel.appendChild(row);
    }
  }, [players, renderPlayerCard]);

  // Find winner position in player array
  const findWinnerPosition = useCallback((winner) => {
    if (!winner || !players.length) return -1;

    for (let i = 0; i < players.length; i++) {
      const player = players[i];
      if (
        (player && player.address && (player.address === winner.winner || player.address === winner.fullAddress)) ||
        (player && player.fullAddress && (player.fullAddress === winner.winner || player.fullAddress === winner.fullAddress)) ||
        (player && player.username && player.username === winner.username)
      ) {
        return i;
      }
    }
    return -1;
  }, [players]);

    // Start idle movement (constant speed when waiting)
  const startIdleMovement = useCallback(() => {
    if (!wheelRef.current || isAnimating) return;

    let currentPosition = 0;
    const speed = 1; // pixels per frame

    const animate = () => {
      if (!wheelRef.current || isAnimating) return;

      currentPosition -= speed;

      // Reset position when we've moved one full cycle
      const resetPoint = -(players.length * CARD_WIDTH);
      if (currentPosition <= resetPoint) {
        currentPosition = 0;
      }

      wheelRef.current.style.transform = `translate3d(${currentPosition}px, 0px, 0px)`;

      if (isIdleMoving && !isAnimating) {
        idleAnimationRef.current = requestAnimationFrame(animate);
      }
    };

    idleAnimationRef.current = requestAnimationFrame(animate);
  }, [players.length, CARD_WIDTH, isAnimating, isIdleMoving]);

  // Stop idle movement
  const stopIdleMovement = useCallback(() => {
    setIsIdleMoving(false);
    if (idleAnimationRef.current) {
      cancelAnimationFrame(idleAnimationRef.current);
    }
  }, []);

  // Spin wheel function (based on roulette logic)
  const spinWheel = useCallback((targetWinner) => {
    if (isAnimating || !wheelRef.current) return;

    // Stop idle movement first
    stopIdleMovement();
    setIsAnimating(true);

    const winnerPosition = findWinnerPosition(targetWinner);
    if (winnerPosition === -1) {
      console.warn('Winner not found in player list');
      setIsAnimating(false);
      setIsIdleMoving(true);
      return;
    }

    // Get current position
    const currentTransform = wheelRef.current.style.transform;
    const currentMatch = currentTransform.match(/translate3d\((-?\d+(?:\.\d+)?)px/);
    const currentPosition = currentMatch ? parseFloat(currentMatch[1]) : 0;

    // Calculate landing position (roulette logic)
    const rows = 12; // Number of rows to spin through
    const landingPosition = (rows * players.length * CARD_WIDTH) + (winnerPosition * CARD_WIDTH);

    // Add randomization for natural landing
    const randomize = Math.floor(Math.random() * CARD_WIDTH) - (CARD_WIDTH / 2);
    const finalPosition = currentPosition - landingPosition + randomize;

    // Random easing curve for dynamic animation
    const randomX = Math.floor(Math.random() * 50) / 100;
    const randomY = Math.floor(Math.random() * 20) / 100;

    // Apply CSS transition
    const wheel = wheelRef.current;
    wheel.style.transitionTimingFunction = `cubic-bezier(0, ${randomX}, ${randomY}, 1)`;
    wheel.style.transitionDuration = '6s';
    wheel.style.transform = `translate3d(${finalPosition}px, 0px, 0px)`;

    // Reset after animation
    setTimeout(() => {
      wheel.style.transitionTimingFunction = '';
      wheel.style.transitionDuration = '';

      const resetTo = -(winnerPosition * CARD_WIDTH + randomize);
      wheel.style.transform = `translate3d(${resetTo}px, 0px, 0px)`;

      setIsAnimating(false);
      setIsIdleMoving(true); // Resume idle movement
      onSpinComplete?.(targetWinner);
    }, 6000);

  }, [isAnimating, findWinnerPosition, players.length, onSpinComplete, CARD_WIDTH, stopIdleMovement]);

  // Handle spinning trigger
  useEffect(() => {
    if (isSpinning && contractWinner && !isAnimating) {
      console.log('Starting roulette spin with winner:', contractWinner);
      spinWheel(contractWinner);
    }
  }, [isSpinning, contractWinner, isAnimating, spinWheel]);

  // Initialize wheel when players change
  useEffect(() => {
    initWheel();
  }, [initWheel]);

  if (!players || players.length === 0) {
    return (
      <div className="simple-carousel">
        <div className="carousel-message">
          Waiting for players to join...
        </div>
      </div>
    );
  }

  return (
    <div className="roulette-wrapper">
      {/* Center selector line */}
      <div className="roulette-selector"></div>

      {/* Spinning wheel */}
      <div ref={wheelRef} className="roulette-wheel"></div>

      {/* Glare Effect */}
      <div 
        className="absolute -top-1/4 -right-1/3 h-full opacity-75 will-change-transform z-[3] pointer-events-none"
        style={{ maskImage: 'linear-gradient(black, transparent 50%)' }}
      >
        <img src="/img/glare.webp" className="w-full object-cover object-center" alt="" />
              </div>
    </div>
  );
};

export default SimpleCarousel;
