import { useEffect, useState } from 'react';

const useTelegramWebApp = () => {
  const [webApp, setWebApp] = useState(null);
  const [user, setUser] = useState(null);
  const [isExpanded, setIsExpanded] = useState(false);
  const [initData, setInitData] = useState(null);

  useEffect(() => {
    const app = window.Telegram?.WebApp;
    
    if (app) {
      app.ready();
      app.expand();
      
      // Set theme colors to match our design
      app.setHeaderColor('#1a1b2e');
      app.setBackgroundColor('#1a1b2e');
      
      // Enable closing confirmation
      app.enableClosingConfirmation();
      
      setWebApp(app);
      
      // Get real user data
      const userData = app.initDataUnsafe?.user;
      const rawInitData = app.initData;
      
      if (userData) {
        setUser({
          id: userData.id,
          firstName: userData.first_name,
          lastName: userData.last_name,
          username: userData.username,
          languageCode: userData.language_code,
          isPremium: userData.is_premium || false,
          photoUrl: userData.photo_url,
          // Formatted display name
          displayName: userData.first_name + (userData.last_name ? ` ${userData.last_name}` : ''),
          // Short name for UI
          shortName: userData.first_name || userData.username || 'User'
        });
        
        setInitData(rawInitData);
        console.log('ðŸ“± Real Telegram user data loaded:', userData);
      } else {
        // Fallback to mock data for testing
        console.log('ðŸ“± No Telegram user data - using mock');
        setUser({
          id: 123456789,
          firstName: "Test",
          lastName: "User", 
          username: "testuser",
          languageCode: "en",
          isPremium: false,
          displayName: "Test User",
          shortName: "Test",
          photoUrl: null
        });
      }
      
      setIsExpanded(true);
      
      // Handle back button
      app.BackButton.onClick(() => {
        app.close();
      });
      
    } else {
      console.log('Telegram WebApp not available - running in browser mode');
      // Set mock user for browser testing
      setUser({
        id: 123456789,
        firstName: "Test",
        lastName: "User",
        username: "testuser", 
        languageCode: "en",
        isPremium: false,
        displayName: "Test User",
        shortName: "Test",
        photoUrl: null
      });
    }
  }, []);

  const showAlert = (message) => {
    if (webApp) {
      webApp.showAlert(message);
    } else {
      window.alert(message);
    }
  };

  const showConfirm = (message, callback) => {
    if (webApp) {
      webApp.showConfirm(message, callback);
    } else {
      callback(window.confirm(message));
    }
  };

  const hapticFeedback = (type = 'impact') => {
    if (webApp?.HapticFeedback) {
      switch (type) {
        case 'light':
          webApp.HapticFeedback.impactOccurred('light');
          break;
        case 'medium':
          webApp.HapticFeedback.impactOccurred('medium');
          break;
        case 'heavy':
          webApp.HapticFeedback.impactOccurred('heavy');
          break;
        case 'error':
          webApp.HapticFeedback.notificationOccurred('error');
          break;
        case 'success':
          webApp.HapticFeedback.notificationOccurred('success');
          break;
        case 'warning':
          webApp.HapticFeedback.notificationOccurred('warning');
          break;
        default:
          webApp.HapticFeedback.impactOccurred('medium');
      }
    }
  };

  // Open external link (for wallet connections)
  const openLink = (url) => {
    if (webApp) {
      webApp.openLink(url);
    } else {
      window.open(url, '_blank');
    }
  };

  // Show main button (for betting actions)
  const showMainButton = (text, onClick) => {
    if (webApp?.MainButton) {
      webApp.MainButton.setText(text);
      webApp.MainButton.show();
      webApp.MainButton.onClick(onClick);
    }
  };

  const hideMainButton = () => {
    if (webApp?.MainButton) {
      webApp.MainButton.hide();
    }
  };

  // Get user avatar URL or generate one
  const getUserAvatar = () => {
    if (user?.photoUrl) {
      return user.photoUrl;
    }
    
    // Generate avatar based on user initials
    const initials = user?.shortName?.charAt(0)?.toUpperCase() || 'U';
    return `https://ui-avatars.com/api/?name=${initials}&background=6366f1&color=ffffff&size=128`;
  };

  return {
    webApp,
    user,
    isExpanded,
    initData,
    showAlert,
    showConfirm,
    hapticFeedback,
    openLink,
    showMainButton,
    hideMainButton,
    getUserAvatar,
    isReady: !!webApp,
    isTelegramEnv: !!window.Telegram?.WebApp,
    // Helper to check if we have real user data
    hasRealUserData: !!(webApp && webApp.initDataUnsafe?.user)
  };
};

export default useTelegramWebApp;
