import React, { useRef, useEffect, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay } from 'swiper/modules';
import 'swiper/css';
import './SimpleCarousel.css';

const SimpleCarousel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const swiperRef = useRef(null);
  const [isLanding, setIsLanding] = useState(false);

  // Handle swiper initialization
  const handleSwiperInit = (swiper) => {
    swiperRef.current = swiper;
  };

  // Utility function to safely restart autoplay with new delay
  const restartAutoplayWithDelay = (delay) => {
    if (swiperRef.current && swiperRef.current.autoplay) {
      swiperRef.current.autoplay.stop();
      swiperRef.current.params.autoplay.delay = delay;
      setTimeout(() => {
        if (swiperRef.current && swiperRef.current.autoplay) {
          swiperRef.current.autoplay.start();
        }
      }, 50);
    }
  };

  // Casino-style winner landing animation
  useEffect(() => {
    if (winner && swiperRef.current && players) {
      const winnerIndex = players.findIndex(p => 
        p && (
          p.address === winner.address || 
          p.username === winner.username ||
          p.address === winner.winner ||
          p.address === winner.fullAddress ||
          p.username === winner.displayName
        )
      );

      if (winnerIndex !== -1) {
        console.log('🎰 Winner found at index:', winnerIndex, 'for winner:', winner);

        // Create casino-style slowdown animation
        const createCasinoSlowdown = () => {
          // Calculate target slide index (account for 6 sets, aim for middle set)
          const totalSlides = players.length * 6;
          const middleSetStart = players.length * 2; // Start of 3rd set (middle)
          const targetSlideIndex = middleSetStart + winnerIndex;

          console.log('🎯 Targeting slide:', targetSlideIndex, 'out of', totalSlides);

                     // Phase 1: Continue fast spinning for 1 second (using performance timer)
           const phase1StartTime = performance.now();
           const waitForPhase2 = () => {
             if (performance.now() - phase1StartTime >= 1000) {
               // Phase 2: Gradual slowdown (like a real casino)
               if (swiperRef.current && swiperRef.current.autoplay) {
                 // Slow down gradually
                 swiperRef.current.autoplay.stop();

                 let currentDelay = 50;
                 const slowdownSteps = [100, 200, 400, 600, 800, 1000];
                 let stepIndex = 0;

                             const gradualSlowdown = () => {
                 if (stepIndex < slowdownSteps.length && swiperRef.current) {
                   currentDelay = slowdownSteps[stepIndex];

                   // Restart autoplay with slower speed using utility function
                   restartAutoplayWithDelay(currentDelay);

                   stepIndex++;

                   // Use combination of setTimeout and requestAnimationFrame for reliability
                   const nextStepDelay = currentDelay * 3;
                   const startTime = performance.now();

                   const waitForNextStep = () => {
                     if (performance.now() - startTime >= nextStepDelay) {
                       gradualSlowdown();
                     } else {
                       requestAnimationFrame(waitForNextStep);
                     }
                   };

                   requestAnimationFrame(waitForNextStep);
                 } else {
                                     // Final phase: Stop and land on winner with casino-style precision
                   if (swiperRef.current) {
                     swiperRef.current.autoplay.stop();

                                           // Add some suspense before final landing (like real casinos)
                      setTimeout(() => {
                        setIsLanding(true); // Apply casino landing styles

                        if (swiperRef.current && typeof swiperRef.current.slideTo === 'function') {
                          // Get current active index for smoother transition
                          const currentIndex = swiperRef.current.activeIndex || 0;

                          // Calculate the shortest path to winner (considering loop)
                          let finalTargetIndex = targetSlideIndex;
                          const totalSlides = players.length * 6;

                          // Ensure we land on the correct winner in the visible range
                          if (Math.abs(finalTargetIndex - currentIndex) > totalSlides / 2) {
                            // Take the shorter route
                            if (finalTargetIndex > currentIndex) {
                              finalTargetIndex = finalTargetIndex - totalSlides;
                            } else {
                              finalTargetIndex = finalTargetIndex + totalSlides;
                            }
                          }

                          // Smooth slide to winner with easing
                          swiperRef.current.slideTo(finalTargetIndex, 2000, true);
                          console.log('🏆 Final landing: current:', currentIndex, 'target:', finalTargetIndex);
                        }

                        setTimeout(() => {
                          setIsLanding(false); // Remove landing styles
                          onWinnerLand?.();
                        }, 2000);
                      }, 500); // Half-second suspense
                   }
                }
              };

                             gradualSlowdown();
             }
           } else {
             requestAnimationFrame(waitForPhase2);
           }
         };

         requestAnimationFrame(waitForPhase2);
        };

        createCasinoSlowdown();
      } else {
        console.warn('⚠️ Winner not found in players array:', winner);
      }
    }
  }, [winner, players, onWinnerLand]);

  // Safety mechanism: restart normal animation if spinning stops without winner
  useEffect(() => {
    if (!isSpinning && !winner && swiperRef.current) {
      // Ensure autoplay continues with normal speed
      restartAutoplayWithDelay(300);
    }
  }, [isSpinning, winner]);

  // Handle browser tab visibility changes to ensure animation continues
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden && swiperRef.current) {
        console.log('🔄 Tab became visible - ensuring animation continues');

        // Force restart autoplay when tab becomes visible
        setTimeout(() => {
          if (swiperRef.current && swiperRef.current.autoplay) {
            const currentDelay = isSpinning ? 50 : 300;
            restartAutoplayWithDelay(currentDelay);
          }
        }, 100);
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => document.removeEventListener('visibilitychange', handleVisibilityChange);
  }, [isSpinning]);

  // Force animation to continue on window focus
  useEffect(() => {
    const handleWindowFocus = () => {
      if (swiperRef.current && swiperRef.current.autoplay) {
        console.log('🔄 Window focused - restarting animation');
        const currentDelay = isSpinning ? 50 : 300;
        restartAutoplayWithDelay(currentDelay);
      }
    };

    window.addEventListener('focus', handleWindowFocus);
    return () => window.removeEventListener('focus', handleWindowFocus);
  }, [isSpinning]);

  return (
    <div className="simple-carousel-container">
      {/* Target Arrow */}
      <div className="carousel-arrow">⬇️</div>

      <Swiper
        ref={swiperRef}
        modules={[Autoplay]}
        spaceBetween={10}
        slidesPerView="auto"
        centeredSlides={true}
        loop={true}
        loopedSlides={players.length * 6} // More slides for smoother infinite scroll
        autoplay={{
          delay: isSpinning ? 50 : 300, // Smoother spinning animation (50ms vs 10ms)
          disableOnInteraction: false,
          pauseOnMouseEnter: false,
          waitForTransition: false, // Allow smooth transitions during speed changes
          stopOnLastSlide: false,
          reverseDirection: false,
          pauseOnTabHidden: false, // Keep animation running when tab is hidden
          pauseOnVisibilityChange: false, // Don't pause when tab visibility changes
        }}
        speed={isSpinning ? 100 : 400} // Smoother transition speeds
        className={`player-swiper ${isSpinning ? 'spinning' : ''} ${isLanding ? 'landing' : ''}`}
        allowTouchMove={false}
        onSwiper={handleSwiperInit}
      >
        {/* Create 6 sets for much smoother infinite scroll */}
        {[...Array(6)].map((_, setIndex) => 
          players.map((player, index) => (
            <SwiperSlide key={`${setIndex}-${player?.address || `waiting-${index}`}`} className="player-slide">
              <div className={`player-card ${
                winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) ? 'winner' : player ? 'active' : 'waiting'
              }`}>
                <div className="player-avatar">
                  {player ? (
                    <div className="player-avatar-content">
                      {player.username ? player.username[0].toUpperCase() : '?'}
                    </div>
                  ) : (
                    <div className="waiting-icon">❓</div>
                  )}
                </div>
                <div className="player-info">
                  <div className="player-name">
                    {player ? (player.username || player.displayName || 'Player') : 'Waiting'}
                  </div>
                  <div className="player-bet">
                    {player ? `${(player.amount || 0).toFixed(2)} TON` : '0.00 TON'}
                  </div>
                  {winner && player && (
                    player.address === winner.address || 
                    player.username === winner.username ||
                    player.address === winner.winner ||
                    player.address === winner.fullAddress ||
                    player.username === winner.displayName
                  ) && (
                    <div className="winner-prize">
                      Won {parseFloat(winner.prize || 0).toFixed(3)} TON
                    </div>
                  )}
                </div>
                {winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) && (
                  <div className="winner-crown">👑</div>
                )}
              </div>
            </SwiperSlide>
          ))
        )}
      </Swiper>
    </div>
  );
};

export default SimpleCarousel;
