import React, { useState, useEffect, useRef, useCallback } from 'react';
import './SimpleCarousel.css';
import { TonIcon } from './IconComponents';

const SimpleCarousel = ({ 
  players, 
  isSpinning, 
  onSpinComplete, 
  contractWinner,
  totalJackpotValue 
}) => {
  const [translateX, setTranslateX] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const animationRef = useRef();
  const containerRef = useRef();
  const [centerIndex, setCenterIndex] = useState(10); // Center player

  // Create extended player list for infinite scrolling
  const extendedPlayers = React.useMemo(() => {
    if (!players || players.length === 0) return [];

    // Create multiple copies for smooth infinite scrolling
    const copies = [];
    for (let i = 0; i < 8; i++) {
      copies.push(...players);
    }
    return copies;
  }, [players]);

  // Calculate player slot width (assuming 166px width + gap)
  const SLOT_WIDTH = 180; // 166px + gap
  const CENTER_POSITION = -((centerIndex * SLOT_WIDTH) - (window.innerWidth / 2) + (SLOT_WIDTH / 2));

  // Find winner in the extended players list
  const findWinnerIndex = useCallback((winner) => {
    if (!winner || !extendedPlayers.length) return -1;

    // Find the winner in the middle section of extended players to avoid edge cases
    const startIndex = players.length * 2; // Start from 3rd copy
    const endIndex = players.length * 5; // End at 6th copy

    for (let i = startIndex; i < endIndex && i < extendedPlayers.length; i++) {
      const player = extendedPlayers[i];
      if (
        (player.address && (player.address === winner.winner || player.address === winner.fullAddress)) ||
        (player.fullAddress && (player.fullAddress === winner.winner || player.fullAddress === winner.fullAddress)) ||
        (player.username && player.username === winner.username)
      ) {
        return i;
      }
    }
    return -1;
  }, [extendedPlayers, players]);

  // Start spinning animation
  const startSpin = useCallback((targetWinner) => {
    if (isAnimating) return;

    setIsAnimating(true);

    const winnerIndex = findWinnerIndex(targetWinner);
    if (winnerIndex === -1) {
      console.warn('Winner not found in player list');
      setIsAnimating(false);
      return;
    }

    // Calculate target rotation to position winner at the top (pointer position)
    const winnerAngle = (winnerIndex % 12) * 30; // 30 degrees per position (360/12)
    const targetRotation = -winnerAngle; // Negative to rotate counter-clockwise

    // Add multiple full rotations for dramatic effect (3-5 full spins)
    const fullRotations = 360 * (3 + Math.random() * 2); // 3-5 full rotations
    const finalTarget = targetRotation - fullRotations;

    let currentRotation = translateX;
    const startTime = Date.now();
    const duration = 4000; // 4 seconds for more dramatic effect

    const animate = () => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min(elapsed / duration, 1);

      // Easing function for smooth deceleration (cubic ease-out)
      const easeOut = 1 - Math.pow(1 - progress, 3);

      const newRotation = currentRotation + (finalTarget - currentRotation) * easeOut;
      setTranslateX(newRotation);

      if (progress < 1) {
        animationRef.current = requestAnimationFrame(animate);
      } else {
        setIsAnimating(false);
        setCenterIndex(winnerIndex);
        onSpinComplete?.(targetWinner);
      }
    };

    animationRef.current = requestAnimationFrame(animate);
  }, [isAnimating, findWinnerIndex, translateX, players.length, onSpinComplete]);

  // Handle spinning trigger
  useEffect(() => {
    if (isSpinning && contractWinner && !isAnimating) {
      startSpin(contractWinner);
    }
  }, [isSpinning, contractWinner, isAnimating, startSpin]);

  // Cleanup animation on unmount
  useEffect(() => {
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  // Render player slot
  const renderPlayerSlot = (player, index, isWinner = false) => {
    if (!player) return null;

    const isActive = player.isRealPlayer && player.bet > 0;
    const chance = totalJackpotValue > 0 ? (player.bet * 100) / totalJackpotValue : 0;

    return (
      <div 
        key={`player-${index}`}
        className="w-auto h-full aspect-[166/191] -mt-1 relative"
        style={{
          color: isActive ? 'rgb(34, 144, 191)' : 'rgb(89, 89, 89)',
          transform: 'scale(1)',
          transitionDelay: '0s'
        }}
      >
        <div className={`w-full h-full preserve-3d backface-hidden will-change-transform ${isActive ? 'animate-popup-enter' : ''}`}>
          <div className="flex flex-col justify-center aspect-[1/1.15] bg-gradient-to-b from-[#FFFFFF]/15 to-[#2A2E37]/0 rounded-[12px] p-[2px] w-full h-full">
            <div 
              className="bg-[#141414] w-full h-full absolute top-0 left-0 z-[3] rounded-[12px] duration-500 hidden sm:block transition-opacity will-change-opacity"
              style={{ opacity: isActive ? '0' : '0.5' }}
            />

            <div 
              className="flex flex-col h-full rounded-[10px] shadow-bet transition-[background-color] duration-300 will-change-[background-color]"
              style={{ backgroundColor: isActive ? 'rgb(35, 46, 66)' : 'rgb(25, 25, 25)' }}
            >
              <img 
                src="/img/waiting-glare.webp" 
                className="w-full object-cover object-center absolute top-0 left-0 z-[2] rounded-[11px] opacity-50 pointer-events-none" 
                alt="" 
              />
              <img 
                src="/img/player-pattern.webp" 
                className="w-full object-cover object-center absolute bottom-0 left-0 z-[2] rounded-b-[11px] pointer-events-none" 
                alt="" 
              />

              <div className="w-full h-full rounded-[10px] bg-gradient-to-b from-[#FFFFFF]/15 to-[#2A2E37]/0 p-[2px] grow relative">
                <div 
                  className="flex flex-col items-center justify-center w-full h-full rounded-[8px] p-2 transition-[background-color,color] duration-300 will-change-[background-color,color]"
                  style={{ 
                    backgroundColor: isActive ? 'rgb(35, 46, 66)' : 'rgb(25, 25, 25)',
                    color: isActive ? 'rgb(34, 144, 191)' : 'rgb(89, 89, 89)'
                  }}
                >
                  <div className="relative w-full h-max mt-2">
                    {isActive && (
                      <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 z-0 w-[100%] h-[150%]" style={{ maskImage: 'linear-gradient(transparent, black, transparent)' }}>
                        <div className="w-full h-full grid-background opacity-15" style={{ maskImage: 'linear-gradient(to right, transparent, black, transparent)' }} />
                      </div>
                    )}

                    <div className="relative w-full translate-z-10">
                      <div className="w-[45%] relative m-auto">
                        <div 
                          className="rounded-[11px] overflow-hidden border-[1px] border-[#222222] aspect-square hover:brightness-125 w-full h-auto -mt-1 relative z-10 transition-[opacity,filter] duration-300 will-change-[opacity,filter] cursor-pointer shadow-[0px_1.48px_0px_0px_#FFFFFF1A_inset] bg-[#303045] p-[1px] border-none"
                          style={{ 
                            borderRadius: '14px',
                            opacity: isActive ? '1' : '0.4',
                            pointerEvents: isActive ? 'auto' : 'none'
                          }}
                        >
                          <div className="w-full h-full p-0.5 border-[1px] border-[#222222] bg-current relative overflow-hidden" style={{ borderRadius: '13px' }}>
                            <div className="bg-gradient-to-b from-white to-white/0 opacity-30 absolute top-0 left-0 w-full h-full" />
                            <div className="w-full h-full border-[1px] border-[#222222] overflow-hidden shadow-avatar-emboss relative z-[3] bg-[#595959]" style={{ borderRadius: '11px' }}>
                              <img 
                                src={player.avatar && player.avatar !== 'â“' ? player.avatar : "/img/unknown.webp"}
                                className="object-cover object-center w-full h-full" 
                                alt="" 
                                fetchPriority="high" 
                              />
                            </div>
                          </div>
                        </div>

                        <img 
                          src="data:image/webp;base64,"
                          className="object-cover object-center absolute w-full -bottom-1/4 m-auto z-0 scale-y-[2]" 
                          alt="" 
                        />
                      </div>
                    </div>
                  </div>

                  <div className="relative text-sm mt-4">
                    <p className={`max-w-[50px] truncate ${isActive ? 'text-white' : 'text-[#8C8C8C]'}`}>
                      {player.name || 'Waiting'}
                    </p>
                  </div>

                  <div className="relative mt-0.5">
                    <div className="flex items-center">
                      <img 
                        src={isActive ? "https://raw.githubusercontent.com/solana-labs/token-list/main/assets/mainnet/So11111111111111111111111111111111111111112/logo.png" : "/img/solana.png"}
                        className={`object-cover object-center w-[22px] h-[22px] mx-auto mr-1.5 rounded-full ${!isActive ? 'grayscale' : ''}`}
                        alt="" 
                      />
                      <p className={`text-md font-bold transition-colors duration-300 will-change-[color] ${isActive ? 'text-white' : 'text-[#8C8C8C]'}`}>
                        {isActive ? player.bet?.toFixed(3) || '0.000' : '0.000'}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Winner Indicator */}
            <div className="absolute top-0 w-max h-max mx-auto inset-x-0">
              <div 
                className="bg-gradient-to-r from-transparent via-white to-transparent absolute top-0 inset-x-0 mx-auto w-[30px] h-[calc(100%-2px)] mix-blend-plus-lighter transition-opacity duration-300 will-change-[opacity]"
                style={{ opacity: isWinner ? '1' : '0' }}
              />
              <svg 
                width="30" 
                height="3" 
                viewBox="0 0 45 5" 
                fill="none" 
                xmlns="http://www.w3.org/2000/svg" 
                className={`tab-svg inset-x-0 mb-auto mx-auto transition-[opacity,color] duration-300 will-change-[opacity,color] ${isActive ? 'opacity-100' : 'opacity-25'}`}
                style={{ color: isWinner ? 'rgb(81, 151, 233)' : 'rgb(196, 196, 196)' }}
              >
                <path d="M3.47326 4.29086C2.91607 4.29086 2.40153 3.99253 2.12472 3.50896L1.01431 1.56914C0.717824 1.0512 1.09177 0.40625 1.68857 0.40625L43.2933 0.406253C43.8901 0.406253 44.2641 1.0512 43.9676 1.56915L42.8572 3.50896C42.5803 3.99253 42.0658 4.29087 41.5086 4.29087L3.47326 4.29086Z" fill="currentColor"/>
              </svg>
            </div>
          </div>

          {/* Drop Shadow */}
          <div 
            className="w-full h-8 absolute bg-black rounded-[100%] -bottom-[20%] -z-[1] blur-lg opacity-50"
            style={{ transform: 'translateY(5px) scaleY(0.4)' }}
          />
        </div>
      </div>
    );
  };

  return (
    <div className="w-full h-full relative z-[4] flex items-center justify-center">
      {/* Circular Wheel Container */}
      <div 
        className="relative w-80 h-80 rounded-full border-4 border-purple-500/30 bg-gradient-to-br from-purple-900/20 to-blue-900/20 backdrop-blur-sm"
        style={{ 
          transform: `rotate(${translateX / 10}deg)`,
          transition: isAnimating ? 'none' : 'transform 0.3s ease-out'
        }}
        ref={containerRef}
      >
        {/* Center Hub */}
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="w-16 h-16 rounded-full bg-gradient-to-br from-purple-600 to-blue-600 border-4 border-white/20 flex items-center justify-center shadow-2xl">
            <div className="text-2xl">ðŸŽ°</div>
          </div>
        </div>

        {/* Player Cards arranged in circle */}
        {extendedPlayers.slice(0, 12).map((player, index) => {
          const angle = (index * 360) / 12; // 12 positions around the circle
          const radius = 120; // Distance from center
          const x = Math.cos((angle - 90) * Math.PI / 180) * radius;
          const y = Math.sin((angle - 90) * Math.PI / 180) * radius;

          const isWinner = contractWinner && player && (
            (player.address && (player.address === contractWinner.winner || player.address === contractWinner.fullAddress)) ||
            (player.fullAddress && (player.fullAddress === contractWinner.winner || player.fullAddress === contractWinner.fullAddress)) ||
            (player.username && player.username === contractWinner.username)
          );

          return (
            <div
              key={`wheel-player-${index}`}
              className="absolute w-16 h-20 transform -translate-x-1/2 -translate-y-1/2"
              style={{
                left: `calc(50% + ${x}px)`,
                top: `calc(50% + ${y}px)`,
                transform: `translate(-50%, -50%) rotate(${-translateX / 10}deg)`, // Counter-rotate to keep cards upright
                zIndex: isWinner ? 20 : 10
              }}
            >
              {renderPlayerSlot(player, index, isWinner)}
            </div>
          );
        })}

        {/* Winner Pointer */}
        <div className="absolute -top-6 left-1/2 transform -translate-x-1/2 z-30">
          <div className="w-0 h-0 border-l-4 border-r-4 border-b-8 border-l-transparent border-r-transparent border-b-yellow-400 drop-shadow-lg">
          </div>
        </div>
      </div>

      {/* Glare Effect */}
      <div 
        className="absolute inset-0 opacity-30 will-change-transform z-[2] pointer-events-none rounded-full"
        style={{ 
          background: 'radial-gradient(circle at 30% 30%, rgba(255,255,255,0.3) 0%, transparent 50%)',
          maskImage: 'radial-gradient(circle, black 60%, transparent 80%)'
        }}
      />
    </div>
  );
};

export default SimpleCarousel;
