import React, { useRef, useEffect, useState, useCallback } from 'react';
import './SimpleCarousel.css';
import { TonIcon } from './IconComponents';

const SimpleCarousel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const carouselRef = useRef(null);
  const animationRef = useRef(null);
  const [winnerAnimationActive, setWinnerAnimationActive] = useState(false);
  const [winnerLocked, setWinnerLocked] = useState(false);
  const [currentPosition, setCurrentPosition] = useState(-10); // Start with offset to see immediate movement
  const [debugCounter, setDebugCounter] = useState(0); // Debug counter to test state updates
  const winnerTimeoutRef = useRef(null);
  const lastWinnerRef = useRef(null);
  const animationAttemptsRef = useRef(0);

  // Create extended players array for seamless loop
  const extendedPlayers = React.useMemo(() => {
    let playersToUse = players;

    // If no players, create default waiting cards
    if (!players || players.length === 0) {
      playersToUse = Array.from({ length: 6 }, (_, i) => ({
        id: `waiting-${i}`,
        username: 'Waiting',
        amount: 0,
        isWaiting: true
      }));
    }

    // Create multiple sets for smooth infinite scroll
    const sets = 5;
    const extended = [];
    for (let i = 0; i < sets; i++) {
      extended.push(...playersToUse);
    }

    console.log('üîç Extended players created:', extended.length, 'cards');
    return extended;
  }, [players]);

  // Card width for calculations
  const cardWidth = 125; // 120px width + 4px spacing + 1px for smooth scroll

  // Reset to idle state
  const resetToIdle = useCallback(() => {
    console.log('üéÆ Resetting carousel to idle state');
    setWinnerAnimationActive(false);
    setWinnerLocked(false);
    lastWinnerRef.current = null;
    animationAttemptsRef.current = 0;

    if (winnerTimeoutRef.current) {
      clearTimeout(winnerTimeoutRef.current);
      winnerTimeoutRef.current = null;
    }

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }
  }, []);

    // Start continuous scrolling with requestAnimationFrame
  const startScrolling = useCallback((speed = 'normal') => {
    console.log('‚ñ∂Ô∏è Starting RAF scroll with speed:', speed, 'players:', players?.length || 0);

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }

    const playersLength = players?.length || 6;
    let lastTime = 0;
         const frameDelay = speed === 'fast' ? 50 : 150; // Slower updates to reduce lag

         const animate = (currentTime) => {
       if (currentTime - lastTime >= frameDelay) {
         setCurrentPosition(prev => {
           const newPos = prev - 3; // Reduced movement for smoother animation
           const resetPoint = -(playersLength * cardWidth);
           return newPos <= resetPoint ? -10 : newPos;
         });

         setDebugCounter(c => c + 1);
         lastTime = currentTime;
       }

       animationRef.current = requestAnimationFrame(animate);
     };

    animationRef.current = requestAnimationFrame(animate);
    console.log('‚úÖ RAF Scroll animation started');
  }, [players?.length, cardWidth]);

  // Stop scrolling
  const stopScrolling = useCallback(() => {
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
      console.log('‚èπÔ∏è RAF Animation stopped');
    }
  }, []);

  // Find winner position in players array
  const findWinnerPosition = useCallback(() => {
    if (!winner || !players) return -1;

    const winnerUsername = winner?.username || winner?.displayName || winner?.name;
    const winnerAddress = winner?.address || winner?.winner || winner?.fullAddress;

    for (let i = 0; i < players.length; i++) {
      const player = players[i];
      if (!player) continue;

      const isMatch = (player.address === winnerAddress) ||
                     (player.username === winnerUsername) ||
                     (player.displayName === winnerUsername) ||
                     (winnerAddress && player.address === winnerAddress);

      if (isMatch) {
        console.log('üéØ Found winner at position:', i, 'Player:', player.username || player.displayName);
        return i;
      }
    }

    return -1;
  }, [winner, players]);

  // Smooth land on winner
  const landOnWinner = useCallback(() => {
    if (!carouselRef.current || !winner || winnerAnimationActive) return;

    const winnerIndex = findWinnerPosition();
    if (winnerIndex === -1) {
      console.error('‚ùå Could not find winner position');
      return;
    }

    console.log('üéØ Landing on winner at position:', winnerIndex);
    setWinnerAnimationActive(true);
    stopScrolling();

    // Calculate target position to center the winner
    const containerWidth = carouselRef.current.offsetWidth;
    const centerOffset = containerWidth / 2 - cardWidth / 2;
    const targetPosition = -(winnerIndex * cardWidth) + centerOffset;

    // Add dramatic spinning - multiple full rotations
    const fullRotations = 2 + Math.random(); // 2-3 full spins
    const fullRotationDistance = players.length * cardWidth * fullRotations;
    const finalPosition = targetPosition - fullRotationDistance;

    // Apply smooth CSS transition
    const carousel = carouselRef.current;
    carousel.style.transition = 'transform 3s cubic-bezier(0.25, 0.1, 0.25, 1)';
    carousel.style.transform = `translateX(${finalPosition}px)`;

    // Lock winner after animation
    winnerTimeoutRef.current = setTimeout(() => {
      console.log('üîí Winner locked!');
      setWinnerLocked(true);
      setWinnerAnimationActive(false);
      onWinnerLand?.();
    }, 3500);

  }, [winner, winnerAnimationActive, findWinnerPosition, stopScrolling, players.length, cardWidth, onWinnerLand]);

  // Handle spinning state - ALWAYS start scrolling unless winner is active
  useEffect(() => {
    console.log('üîç Scroll state check:', { 
      isSpinning, 
      hasWinner: !!winner, 
      winnerAnimationActive, 
      winnerLocked,
      playersCount: extendedPlayers.length
    });

    if (winnerAnimationActive || winnerLocked) {
      console.log('üö´ Stopping scroll - winner active');
      stopScrolling();
      return;
    }

    if (isSpinning && !winner) {
      console.log('üé∞ Starting FAST spin...');
      startScrolling('fast');
    } else if (!winner) {
      console.log('üé∞ Starting NORMAL scroll...');
      startScrolling('normal'); // Normal continuous scroll
    }

    return () => stopScrolling();
  }, [isSpinning, winner, winnerAnimationActive, winnerLocked, startScrolling, stopScrolling, extendedPlayers.length]);

  // Handle winner animation
  useEffect(() => {
    if (!winner) {
      resetToIdle();
      return;
    }

    if (winnerLocked || winnerAnimationActive) return;

    // Check for duplicate winner
    const currentWinnerKey = `${winner.username || winner.displayName || 'Player'}-${winner.address || winner.winner || ''}`;
    if (currentWinnerKey === lastWinnerRef.current) return;

    if (animationAttemptsRef.current >= 1) {
      console.warn('üö´ Max animation attempts reached');
      setWinnerLocked(true);
      onWinnerLand?.();
      return;
    }

    console.log('üèÜ Winner announced:', winner.username || winner.displayName || 'Player');
    lastWinnerRef.current = currentWinnerKey;
    animationAttemptsRef.current++;

    setTimeout(() => {
      landOnWinner();
    }, 100);

  }, [winner, winnerLocked, winnerAnimationActive, landOnWinner, onWinnerLand, resetToIdle]);

  // Apply current position via CSS transform
  useEffect(() => {
    if (carouselRef.current && !winnerAnimationActive) {
      carouselRef.current.style.transition = 'none';
      carouselRef.current.style.transform = `translateX(${currentPosition}px)`;
    }
  }, [currentPosition, winnerAnimationActive]);

  // Cleanup
  useEffect(() => {
    return () => {
      stopScrolling();
      if (winnerTimeoutRef.current) {
        clearTimeout(winnerTimeoutRef.current);
      }
    };
  }, [stopScrolling]);

  return (
    <div className="simple-carousel-container">
      {/* Debug Info */}
      <div style={{
        position: 'absolute',
        top: '-50px',
        left: '0',
        fontSize: '11px',
        color: '#fff',
        background: 'rgba(0,0,0,0.8)',
        padding: '4px 8px',
        borderRadius: '4px',
        zIndex: 1000
             }}>
         Cards: {extendedPlayers.length} | Pos: {currentPosition.toFixed(0)}px | 
         Counter: {debugCounter} | Spinning: {isSpinning ? 'YES' : 'NO'} | Winner: {winner ? 'YES' : 'NO'}
       </div>

      {/* Target Arrow */}
      <div className={`carousel-arrow ${
        winnerAnimationActive && !winnerLocked ? 'winner-approaching' : 
        winnerLocked ? 'winner-landed' : ''
      }`}>‚¨áÔ∏è</div>

      {/* Custom Carousel */}
      <div className="carousel-viewport">
        <div 
          ref={carouselRef}
          className={`carousel-track ${isSpinning || winnerAnimationActive ? 'spinning' : ''}`}
        >
          {extendedPlayers.map((player, index) => (
            <div
              key={`${index}-${player?.address || `waiting-${index}`}`}
              className={`player-card ${
                winnerLocked && winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) ? 'winner-final' : player ? 'active' : 'waiting'
              }`}
            >
              <div className="player-avatar">
                {player ? (
                  <>
                    {player.telegramPhotoUrl && (
                      <img 
                        src={player.telegramPhotoUrl} 
                        alt={player.username || 'Player'} 
                        className="telegram-avatar"
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                    )}
                    <div 
                      className="player-avatar-content" 
                      style={{ display: player.telegramPhotoUrl ? 'none' : 'flex' }}
                    >
                      {player.username ? player.username[0].toUpperCase() : '?'}
                    </div>
                  </>
                ) : (
                  <div className="waiting-icon">‚ùì</div>
                )}
              </div>
              <div className="player-info">
                <div className="player-name">
                  {player ? (player.username || player.displayName || 'Player') : 'Waiting'}
                </div>
                <div className="player-bet">
                  <span>{player ? (player.amount || 0).toFixed(2) : '0.00'}</span>
                  <TonIcon size={12} className="ton-icon" />
                </div>
                {winnerLocked && winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) && (
                  <div className="winner-prize">
                    <span>Won {parseFloat(winner.prize || 0).toFixed(3)}</span>
                    <TonIcon size={12} className="ton-icon" />
                  </div>
                )}
              </div>
              {winnerLocked && winner && player && (
                player.address === winner.address || 
                player.username === winner.username ||
                player.address === winner.winner ||
                player.address === winner.fullAddress ||
                player.username === winner.displayName
              ) && (
                <div className="winner-crown">üëë</div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SimpleCarousel;
