import React, { useRef, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, FreeMode } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/free-mode';
import './SimpleCarousel.css';

const SimpleCarousel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const swiperRef = useRef(null);

  // Auto-scroll to winner when announced
  useEffect(() => {
    if (winner && swiperRef.current && players) {
      const winnerIndex = players.findIndex(p => 
        p && (
          p.address === winner.address || 
          p.username === winner.username ||
          p.address === winner.winner ||
          p.address === winner.fullAddress ||
          p.username === winner.displayName
        )
      );
      
      if (winnerIndex !== -1) {
        // Stop autoplay first
        swiperRef.current.autoplay.stop();
        
        // Slide to winner with smooth animation
        swiperRef.current.slideTo(winnerIndex, 2000); // 2 second animation
        setTimeout(() => {
          onWinnerLand?.();
          // Restart autoplay after winner is shown
          setTimeout(() => {
            if (swiperRef.current) {
              swiperRef.current.autoplay.start();
            }
          }, 3000);
        }, 2000);
      }
    }
  }, [winner, players, onWinnerLand]);

  return (
    <div className="simple-carousel-container">
      {/* Target Arrow */}
      <div className="carousel-arrow">‚¨áÔ∏è</div>
      
      <Swiper
        ref={swiperRef}
        modules={[Autoplay, FreeMode]}
        spaceBetween={10}
        slidesPerView="auto"
        centeredSlides={true}
        loop={true}
        freeMode={{
          enabled: true,
          sticky: false,
          momentumRatio: 0.25,
          momentumVelocityRatio: 0.25,
        }}
        autoplay={{
          delay: 0, // Continuous movement
          disableOnInteraction: false,
          pauseOnMouseEnter: false,
          reverseDirection: false,
        }}
        speed={isSpinning ? 8000 : 3000} // Faster when spinning
        className="player-swiper"
        allowTouchMove={false} // Disable manual swiping for pure auto-scroll
      >
        {players.map((player, index) => (
          <SwiperSlide key={`${player?.address || `waiting-${index}`}`} className="player-slide">
            <div className={`player-card ${
              winner && player && (
                player.address === winner.address || 
                player.username === winner.username ||
                player.address === winner.winner ||
                player.address === winner.fullAddress ||
                player.username === winner.displayName
              ) ? 'winner' : player ? 'active' : 'waiting'
            }`}>
              <div className="player-avatar">
                {player ? (
                  <div className="player-avatar-content">
                    {player.username ? player.username[0].toUpperCase() : '?'}
                  </div>
                ) : (
                  <div className="waiting-icon">‚ùì</div>
                )}
              </div>
              <div className="player-info">
                <div className="player-name">
                  {player ? (player.username || player.displayName || 'Player') : 'Waiting'}
                </div>
                <div className="player-bet">
                  {player ? `${(player.amount || 0).toFixed(2)} TON` : '0.00 TON'}
                </div>
                {winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) && (
                  <div className="winner-prize">
                    Won {parseFloat(winner.prize || 0).toFixed(3)} TON
                  </div>
                )}
              </div>
              {winner && player && (
                player.address === winner.address || 
                player.username === winner.username ||
                player.address === winner.winner ||
                player.address === winner.fullAddress ||
                player.username === winner.displayName
              ) && (
                <div className="winner-crown">üëë</div>
              )}
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default SimpleCarousel;
