import React, { useRef, useEffect, useState, useCallback } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay } from 'swiper/modules';
import 'swiper/css';
import './SimpleCarousel.css';

const SimpleCarousel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const swiperRef = useRef(null);
  const [animationPhase, setAnimationPhase] = useState('idle'); // 'idle', 'fast_spin', 'slow_down', 'winner_show'
  const [isAnimating, setIsAnimating] = useState(false);
  const animationTimeoutRef = useRef(null);

  // Handle swiper initialization
  const handleSwiperInit = (swiper) => {
    swiperRef.current = swiper;
  };

  // Simple and reliable winner animation
  const startWinnerAnimation = useCallback((winnerIndex) => {
    if (!swiperRef.current || isAnimating) return;

    console.log('üé∞ Starting winner animation for index:', winnerIndex);
    setIsAnimating(true);

    // Clear any existing timeouts
    if (animationTimeoutRef.current) {
      clearTimeout(animationTimeoutRef.current);
    }

    // Phase 1: Fast spinning (2 seconds)
    setAnimationPhase('fast_spin');
    if (swiperRef.current.autoplay) {
      swiperRef.current.autoplay.stop();
    }

    // Start very fast autoplay for spinning effect
    swiperRef.current.params.autoplay = {
      delay: 10,
      disableOnInteraction: false,
    };
    swiperRef.current.autoplay.start();

    // Phase 2: Slow down (1.5 seconds)
    setTimeout(() => {
      if (swiperRef.current) {
        console.log('üéØ Starting slowdown phase');
        setAnimationPhase('slow_down');

        // Gradually slow down
        swiperRef.current.params.autoplay.delay = 100;
        swiperRef.current.autoplay.start();

        setTimeout(() => {
          if (swiperRef.current) {
            swiperRef.current.params.autoplay.delay = 300;
            swiperRef.current.autoplay.start();
          }
        }, 500);

        setTimeout(() => {
          if (swiperRef.current) {
            swiperRef.current.params.autoplay.delay = 600;
            swiperRef.current.autoplay.start();
          }
        }, 1000);
      }
    }, 2000);

    // Phase 3: Land on winner (slide to exact position)
    setTimeout(() => {
      if (swiperRef.current) {
        console.log('üèÜ Landing on winner at index:', winnerIndex);
        setAnimationPhase('winner_show');

        // Stop autoplay completely
        swiperRef.current.autoplay.stop();

        // Slide to winner with smooth animation
        swiperRef.current.slideTo(winnerIndex, 1500, false);

        // Phase 4: Lock on winner
        setTimeout(() => {
          console.log('üîí Winner locked in position');

          // Completely stop all movement
          if (swiperRef.current) {
            swiperRef.current.autoplay.stop();
            swiperRef.current.allowSlideNext = false;
            swiperRef.current.allowSlidePrev = false;

            // Force stop any ongoing transitions
            const wrapper = swiperRef.current.wrapperEl;
            if (wrapper) {
              setTimeout(() => {
                wrapper.style.transition = 'none';
                // Lock in current position
                const currentTransform = getComputedStyle(wrapper).transform;
                wrapper.style.transform = currentTransform;
              }, 1600); // After slide animation completes
            }

            // Trigger winner callback
            onWinnerLand?.();

            // Keep showing winner for 6 seconds
            animationTimeoutRef.current = setTimeout(() => {
              resetAnimation();
            }, 6000);
          }
        }, 1600); // Wait for slide animation to complete
      }
    }, 3500); // Total of fast + slow phases

  }, [isAnimating, onWinnerLand]);

  // Reset animation to normal state
  const resetAnimation = useCallback(() => {
    console.log('üîÑ Resetting animation to normal state');

    if (swiperRef.current) {
      // Re-enable swiper functionality
      swiperRef.current.allowSlideNext = true;
      swiperRef.current.allowSlidePrev = true;

      // Reset wrapper styles
      const wrapper = swiperRef.current.wrapperEl;
      if (wrapper) {
        wrapper.style.transition = '';
        wrapper.style.transform = '';
      }

      // Reset autoplay to normal speed
      swiperRef.current.params.autoplay = {
        delay: 400,
        disableOnInteraction: false,
      };

      // Only restart autoplay if not in spinning state and no winner
      if (!isSpinning && !winner) {
        swiperRef.current.autoplay.start();
      }
    }

    setAnimationPhase('idle');
    setIsAnimating(false);

    if (animationTimeoutRef.current) {
      clearTimeout(animationTimeoutRef.current);
      animationTimeoutRef.current = null;
    }
  }, [isSpinning, winner]);

  // Handle winner announcement
  useEffect(() => {
    if (winner && swiperRef.current && players && !isAnimating) {
      // Find winner in the FIRST set only (simplify)
      const winnerIndex = players.findIndex(p => 
        p && (
          p.address === winner.address || 
          p.username === winner.username ||
          p.address === winner.winner ||
          p.address === winner.fullAddress ||
          p.username === winner.displayName
        )
      );

      if (winnerIndex !== -1) {
        console.log('üèÜ Winner found at index:', winnerIndex);
        startWinnerAnimation(winnerIndex);
      } else {
        console.warn('‚ùå Winner not found in players array');
      }
    }
  }, [winner, players, isAnimating, startWinnerAnimation]);

  // Handle regular spinning state
  useEffect(() => {
    if (!swiperRef.current) return;

    if (isSpinning && !winner && !isAnimating) {
      // Fast spinning while waiting for winner
      swiperRef.current.params.autoplay = {
        delay: 15,
        disableOnInteraction: false,
      };
      swiperRef.current.autoplay.start();
    } else if (!isSpinning && !winner && !isAnimating && animationPhase === 'idle') {
      // Normal slow movement
      swiperRef.current.params.autoplay = {
        delay: 400,
        disableOnInteraction: false,
      };
      swiperRef.current.autoplay.start();
    }
  }, [isSpinning, winner, isAnimating, animationPhase]);

  // Reset when winner is cleared (new round)
  useEffect(() => {
    if (!winner && isAnimating) {
      console.log('üîÑ Winner cleared - resetting animation');
      resetAnimation();
    }
  }, [winner, isAnimating, resetAnimation]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (animationTimeoutRef.current) {
        clearTimeout(animationTimeoutRef.current);
      }
    };
  }, []);

  return (
    <div className="simple-carousel-container">
      {/* Target Arrow */}
      <div className={`carousel-arrow ${
        animationPhase === 'slow_down' ? 'winner-approaching' : 
        animationPhase === 'winner_show' ? 'winner-landed' : ''
      }`}>‚¨áÔ∏è</div>

      <Swiper
        ref={swiperRef}
        modules={[Autoplay]}
        spaceBetween={10}
        slidesPerView="auto"
        centeredSlides={true}
        loop={true}
        loopedSlides={Math.max(players.length, 6)}
        autoplay={{
          delay: isSpinning && !isAnimating ? 15 : 400,
          disableOnInteraction: false,
          pauseOnMouseEnter: false,
        }}
        speed={animationPhase === 'fast_spin' ? 50 : 300}
        className={`player-swiper ${isSpinning || animationPhase === 'fast_spin' ? 'spinning' : ''}`}
        allowTouchMove={false}
        onSwiper={handleSwiperInit}
      >
        {/* Use only 3 sets to reduce complexity */}
        {[...Array(3)].map((_, setIndex) => 
          players.map((player, index) => (
            <SwiperSlide key={`${setIndex}-${player?.address || `waiting-${index}`}`} className="player-slide">
              <div className={`player-card ${
                winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) ? 'winner' : player ? 'active' : 'waiting'
              }`}>
                <div className="player-avatar">
                  {player ? (
                    <div className="player-avatar-content">
                      {player.username ? player.username[0].toUpperCase() : '?'}
                    </div>
                  ) : (
                    <div className="waiting-icon">‚ùì</div>
                  )}
                </div>
                <div className="player-info">
                  <div className="player-name">
                    {player ? (player.username || player.displayName || 'Player') : 'Waiting'}
                  </div>
                  <div className="player-bet">
                    {player ? `${(player.amount || 0).toFixed(2)} TON` : '0.00 TON'}
                  </div>
                  {winner && player && (
                    player.address === winner.address || 
                    player.username === winner.username ||
                    player.address === winner.winner ||
                    player.address === winner.fullAddress ||
                    player.username === winner.displayName
                  ) && (
                    <div className="winner-prize">
                      Won {parseFloat(winner.prize || 0).toFixed(3)} TON
                    </div>
                  )}
                </div>
                {winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) && (
                  <div className="winner-crown">üëë</div>
                )}
              </div>
            </SwiperSlide>
          ))
        )}
      </Swiper>
    </div>
  );
};

export default SimpleCarousel;
