import React from 'react';

const WinnerTestButton = ({ players, onSelectWinner }) => {
  const selectRandomWinner = () => {
    const realPlayers = players.filter(p => p?.isRealPlayer);
    if (realPlayers.length === 0) return;
    
    const randomPlayer = realPlayers[Math.floor(Math.random() * realPlayers.length)];
    
    const winnerData = {
      username: randomPlayer.username || randomPlayer.displayName || 'Player',
      displayName: randomPlayer.username || randomPlayer.displayName || 'Player',
      address: randomPlayer.address,
      winner: randomPlayer.address,
      fullAddress: randomPlayer.address,
      prize: '1.500',
      timestamp: Date.now()
    };
    
    console.log('ðŸ§ª Test: Selecting winner:', winnerData);
    onSelectWinner(winnerData);
  };

  return (
    <button 
      onClick={selectRandomWinner}
      style={{
        position: 'fixed',
        top: '10px',
        right: '10px',
        background: '#ff6b35',
        color: 'white',
        border: 'none',
        padding: '8px 12px',
        borderRadius: '6px',
        fontSize: '12px',
        cursor: 'pointer',
        zIndex: 1000
      }}
    >
      ðŸ§ª Test Winner
    </button>
  );
};

export default WinnerTestButton;
