import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { TonConnectUIProvider } from '@tonconnect/ui-react';
import Chat from './components/Chat';
import GameArea from './components/GameArea';
import MobileChat from './components/MobileChat';
import WalletConnection from './components/WalletConnection';
import WinnerBroadcast from './components/WinnerBroadcast';
import useTelegramWebApp from './hooks/useTelegramWebApp';
import useJackpotContract from './hooks/useJackpotContract';
import socketService from './services/socketService';
import winnerCoordinator from './services/winnerCoordinator';
import { uiOptions } from './config/tonconnect';
import { MessageCircle, Trophy, Users, DollarSign } from 'lucide-react';
import './App.css';
import './components/Header.css';
import { motion, useAnimation, useMotionValue } from 'framer-motion';

// TON Connect manifest URL - use remote manifest to avoid 500 errors
const manifestUrl = "https://raw.githubusercontent.com/Vodka2134156/kzsks/main/manifest.json";

function AppContent() {
  // Local UI state
  const [currentRound, setCurrentRound] = useState(53408);
  const [timeRemaining, setTimeRemaining] = useState(30);
  const [activeTab, setActiveTab] = useState('jackpot');
  const [betAmount, setBetAmount] = useState(0.11);
  const [showWinnerAnnouncement, setShowWinnerAnnouncement] = useState(false);

  // Winner state management (moved from GameArea.js)
  const [waitingForWinner, setWaitingForWinner] = useState(false);
  const [contractWinner, setContractWinner] = useState(null);
  const [postWinnerLoading, setPostWinnerLoading] = useState(false);
  const [, setIsAnyWinnerDisplayActive] = useState(false);
  const [showWinnerVisually, setShowWinnerVisually] = useState(false); // Controls when winner card appears
  const [isInWinnerState, setIsInWinnerState] = useState(false); // Blocks new winner broadcasts

  // Bettors state (managed centrally to avoid duplicates)
  const [gameBettors, setGameBettors] = useState([]);
  const [previousRoundBettors, setPreviousRoundBettors] = useState([]); // Store bettors for winner matching
  const [slotSpinning, setSlotSpinning] = useState(false);
  const carouselRef = useRef(null);

  // Refs to avoid stale closures in socket handlers
  const gameBettorsRef = useRef([]);
  const contractWinnerRef = useRef(null);
  const showWinnerAnnouncementRef = useRef(false);
  const lastWinnerTimestampRef = useRef(null);
  const waitingForWinnerRef = useRef(false);
  const previousRoundBettorsRef = useRef([]);
  const timeRemainingRef = useRef(30);
  const showWinnerVisuallyRef = useRef(false);
  const isInWinnerStateRef = useRef(false);

  // Keep refs in sync with state
  useEffect(() => { gameBettorsRef.current = gameBettors; }, [gameBettors]);
  useEffect(() => { 
    contractWinnerRef.current = contractWinner; 

    // Log winner detection only once when contractWinner changes
    if (contractWinner) {
      console.log('üèÜ WINNER DETECTED FROM BACKEND!', { 
        winnerAddress: contractWinner.winner || contractWinner.fullAddress,
        winnerName: contractWinner.username || contractWinner.displayName || 'Player',
        prize: contractWinner.prize,
        contractWinner
      });
      console.log('üé¨ ANIMATION SHOULD START NOW - waiting for carousel to detect winner');
    } else {
      console.log('‚ùå WINNER CLEARED - contractWinner set to null');
    }
  }, [contractWinner]);
  useEffect(() => { showWinnerAnnouncementRef.current = showWinnerAnnouncement; }, [showWinnerAnnouncement]);
  useEffect(() => { waitingForWinnerRef.current = waitingForWinner; }, [waitingForWinner]);
  useEffect(() => { previousRoundBettorsRef.current = previousRoundBettors; }, [previousRoundBettors]);
  useEffect(() => { timeRemainingRef.current = timeRemaining; }, [timeRemaining]);
  useEffect(() => { showWinnerVisuallyRef.current = showWinnerVisually; }, [showWinnerVisually]);
  useEffect(() => { isInWinnerStateRef.current = isInWinnerState; }, [isInWinnerState]);

  // Centralized winner announcement manager
  const showWinnerAnnouncements = useCallback((winnerData, source = 'unknown') => {
    const winnerTimestamp = winnerData.timestamp;

    // Block winner broadcasts if already in winner state
    if (isInWinnerStateRef.current) {
      console.log(`üö´ BLOCKED - Already in winner state, ignoring winner broadcast from ${source}`);
      return false;
    }

    // Use coordinator to check if should block (pass full winner data for better duplicate detection)
    if (winnerCoordinator.shouldBlock(winnerTimestamp, winnerData)) {
      console.log(`üîÑ Winner announcement blocked by coordinator (${source})`);
      return false;
    }

    console.log(`üéâ Showing winner announcements from ${source}:`, winnerData);
    console.log(`üéØ Entering winner state - blocking future winner broadcasts`);

    // Enter winner state to block future broadcasts
    setIsInWinnerState(true);

    // Set coordination flag via coordinator (pass full winner data)
    winnerCoordinator.setActive(true, winnerTimestamp, winnerData);
    setIsAnyWinnerDisplayActive(true);
    lastWinnerTimestampRef.current = winnerTimestamp;

    // Show App.js winner notification
    setContractWinner(winnerData);
    setShowWinnerAnnouncement(true);
    setWaitingForWinner(false);

    // Start animation - winner visual will appear after animation lands on winner
    setTimeout(() => {
      console.log('üé¨ Animation should have landed on winner - showing winner visually');
      setShowWinnerVisually(true);
    }, 3000); // Give animation time to land properly

    // Winner will stay visible until new round starts (no automatic timeout)
    console.log('üèÜ Winner display will persist until new round is declared by backend');

    return true;
  }, []);

  // Telegram integration
  const { user, hapticFeedback, showAlert, hasRealUserData } = useTelegramWebApp();

  // Smart contract integration
  const {
    contractState,
    userStats,
    adminInfo: rawAdminInfo,
    isLoadingContract,
    isPlacingBet,
    placeBet,
    isConnected,
    address,
  } = useJackpotContract();

  // SAFETY: Ensure adminInfo is always properly structured and never an object that could be rendered
  const adminInfo = useMemo(() => {
    if (!rawAdminInfo || typeof rawAdminInfo !== 'object') {
      return {
        isAutoManaged: false,
        roundDuration: 300,
        minBetsToEnd: 1,
        timerActive: false,
        timeRemaining: 0
      };
    }

    // Extract only primitive values to prevent accidental object rendering
    return {
      isAutoManaged: Boolean(rawAdminInfo.isAutoManaged),
      roundDuration: Number(rawAdminInfo.roundDuration) || 300,
      minBetsToEnd: Number(rawAdminInfo.minBetsToEnd) || 1,
      timerActive: Boolean(rawAdminInfo.timerActive),
      timeRemaining: Number(rawAdminInfo.timeRemaining) || 0,
      // Ensure currentRound is always a number, never an object
      currentRound: typeof rawAdminInfo.currentRound === 'object' 
        ? (rawAdminInfo.currentRound?.roundNumber || 0)
        : (Number(rawAdminInfo.currentRound) || 0)
    };
  }, [rawAdminInfo]);

  // SAFETY: Ensure contractState timer properties are never objects that could be accidentally rendered
  const safeContractState = useMemo(() => {
    if (!contractState) return contractState;

    return {
      ...contractState,
      // Ensure timer object properties are always primitives
      timer: contractState.timer ? {
        isActive: Boolean(contractState.timer.isActive),
        timeRemaining: Number(contractState.timer.timeRemaining) || 0,
        timeElapsed: Number(contractState.timer.timeElapsed) || 0,
        roundNumber: Number(contractState.timer.roundNumber) || 0,
        timerExpired: Boolean(contractState.timer.timerExpired)
      } : undefined,
      // Ensure currentRound is always a number if it exists
      currentRound: typeof contractState.currentRound === 'object' 
        ? (contractState.currentRound?.roundNumber || 0)
        : contractState.currentRound
    };
  }, [contractState]);

  // Use real contract data or fallback to simulated data
  const jackpotValue = contractState.totalJackpot;
  const isLive = contractState.isActive;

  // Calculate user stats from gameBettors data
  const userBetTotal = useMemo(() => {
    if (!isConnected || !address || !gameBettors.length) return 0;

    return gameBettors
      .filter(bettor => 
        bettor.address === address || 
        bettor.walletAddress === address
      )
      .reduce((total, bettor) => total + (bettor.amount || 0), 0);
  }, [gameBettors, address, isConnected]);

  const userWinChance = useMemo(() => {
    if (!isConnected || !address || userBetTotal === 0) return 0;

    // Calculate current round total from gameBettors for immediate updates
    const currentRoundTotal = gameBettors.reduce((total, bettor) => total + (bettor.amount || 0), 0);

    // Use the live total from current bettors, fallback to contract jackpot value
    const liveJackpotValue = currentRoundTotal > 0 ? currentRoundTotal : jackpotValue;

    if (!liveJackpotValue) return 0;

    return (userBetTotal / liveJackpotValue) * 100;
  }, [userBetTotal, jackpotValue, gameBettors, isConnected, address]);

  // Use automation timer or fallback to UI timer
  useEffect(() => {
    // Always sync with backend timer when it changes
    if (contractState.timer?.timeRemaining !== undefined) {
      setTimeRemaining(contractState.timer.timeRemaining);
    } else {
      setTimeRemaining(30);
    }
  }, [contractState.timer?.timeRemaining]);

  // Local countdown timer (runs between backend updates)
  useEffect(() => {
    // Only run countdown when game is live
    if (!isLive) return;

    console.log('‚è∞ Starting local countdown timer...');

    const countdown = setInterval(() => {
      setTimeRemaining(prev => {
        // Continue countdown even at 0 but don't go negative
        if (prev > 0) {
          const newTime = prev - 1;
          console.log('‚è∞ Local countdown:', newTime);

          // When we reach 0, trigger "analyzing bets" state locally
        if (newTime === 0) {
            console.log('‚è∞ Local timer reached 0, entering analyzing state...');
            setWaitingForWinner(true); // This will show "Analyzing Bets..."
        }

        return newTime;
        }

        // Stay at 0 once we reach it (don't go negative)
        return 0;
      });
    }, 1000);

    return () => {
      console.log('‚è∞ Stopping local countdown timer...');
      clearInterval(countdown);
    };
  }, [isLive]); // Restart when isLive changes

  // Framer Motion animation controls
  const carouselAnimation = useAnimation();
  const carouselX = useMotionValue(0);

  // Smooth infinite carousel effect with Framer Motion
  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) {
      console.log('‚ö†Ô∏è CAROUSEL REF NOT READY - animation cannot start');
      return;
    }

    console.log('üé† FRAMER MOTION CAROUSEL ANIMATION STARTING with ref:', carousel);

    let animationId;
    let currentPosition = 0;
    let winnerPositionFound = false;
    let winnerCardVisible = false;
    let targetPosition = null; // For dramatic slow-down effect
    let animationPhase = 'normal'; // 'normal', 'fast-spin', 'slow-reveal'
    // FIXED: Dynamic card width calculation to match carousel rendering
    // This must match the card width used in the actual carousel
    const getDynamicCardWidth = () => {
      const isInTelegram = window.Telegram?.WebApp;
      const screenWidth = isInTelegram ? (window.Telegram.WebApp.viewportWidth || window.innerWidth) : window.innerWidth;

      // Match the logic from GameArea.js carousel rendering
      const baseCardWidth = screenWidth <= 480 ? 120 : 150;
      const gap = screenWidth <= 480 ? 15 : 20;
      const cardPlusGap = baseCardWidth + gap;

             // Only log on first calculation or significant changes
       if (process.env.NODE_ENV === 'development') {
         console.log('üìê Card width:', cardPlusGap, 'px', isInTelegram ? '(Telegram)' : '(Web)');
       }

      return cardPlusGap;
    };

    const cardWidth = getDynamicCardWidth();

    // OPTIMIZED: Cache DOM elements to avoid repeated queries
    let cachedTargetArrow = null;

    // Use refs to get current values without causing re-renders
    const getCurrentBettors = () => gameBettorsRef.current;
    const getPreviousBettors = () => previousRoundBettorsRef.current;
    const getContractWinner = () => contractWinnerRef.current;
    const getWaitingForWinner = () => waitingForWinnerRef.current;
    const getTimeRemaining = () => timeRemainingRef.current;
    const getShowWinnerVisually = () => showWinnerVisuallyRef.current;

    const getBettorsForAnimation = () => {
      const currentBettors = getCurrentBettors();
      const prevBettors = getPreviousBettors();

      // Use the EXACT SAME logic as the carousel rendering
      const bettorsToShow = currentBettors.length > 0 ? currentBettors : 
                           (showWinnerVisuallyRef.current || waitingForWinnerRef.current) ? prevBettors : [];

      console.log('üé† getBettorsForAnimation - bettorsToShow:', bettorsToShow.length, 'bettors');

      // Ensure we have at least 6 items to show (fill with waiting slots if needed) - SAME AS CAROUSEL
      const allItems = [
        ...bettorsToShow,
        ...Array.from({ length: Math.max(0, 6 - bettorsToShow.length) }, () => null)
      ];

      console.log('üé† getBettorsForAnimation - Created array with:', allItems.length, 'items total');
      console.log('üé† Real players:', allItems.filter(item => item !== null).length);
      console.log('üé† Waiting slots:', allItems.filter(item => item === null).length);

      return allItems;
    };

    // FIX: Infinite scroll reset logic with dynamic recalculation
    // The carousel renders 3 identical sets of cards for seamless infinite scroll
    const singleSetLength = 6; // Fixed to match carousel rendering (6 items minimum)

    // Simplified parameters for fake infinity carousel
    const calculateScrollParameters = () => {
      const currentCardWidth = getDynamicCardWidth();

      return {
        cardWidth: currentCardWidth,
        containerWidth: window.innerWidth
      };
    };

    // Initial calculation
    let scrollParams = calculateScrollParameters();

    if (process.env.NODE_ENV === 'development') {
      console.log('üé† Carousel animation started - Card width:', scrollParams.cardWidth, 'px');
    }

    // Simple continuous scroll for normal state
    const startNormalAnimation = () => {
      carouselAnimation.start({
        x: -10000, // Scroll far left continuously
        transition: {
          duration: 10000 / 24, // 24px per frame = TRIPLED SPEED!
          ease: "linear",
          repeat: Infinity,
          repeatType: "loop"
        }
      });
    };

    // Fast spin animation for winner selection
    const startFastSpinAnimation = () => {
      carouselAnimation.start({
        x: -10000, // Scroll far left continuously
        transition: {
          duration: 10000 / 72, // 72px per frame = SUPER FAST SPIN!
          ease: "linear",
          repeat: Infinity,
          repeatType: "loop"
        }
      });
    };

    // Two-phase winner landing animation
    const animateToWinner = async (winnerOffset) => {
      console.log('üé∞ Starting two-phase winner animation to offset:', winnerOffset);

      // Phase 1: Fast scroll with overshoot for drama
      await carouselAnimation.start({
        x: winnerOffset - 1000, // Overshoot for suspense
        transition: { 
          duration: 3, 
          ease: "linear" 
        }
      });

      // Phase 2: Precise landing on winner
      await carouselAnimation.start({
        x: winnerOffset, // Land exactly on winner
        transition: { 
          duration: 2, 
          ease: [0.25, 1, 0.5, 1] // Smooth deceleration
        }
      });

      console.log('üéØ Winner animation complete! Landed at:', winnerOffset);
    };

    let animationLoopId;

    const animate = () => {
      // Get current values using refs
      const currentWinner = getContractWinner();
      const isWaiting = getWaitingForWinner();
      const timeLeft = getTimeRemaining();

      // PHASE 1: Normal scrolling or waiting phase
      if (!currentWinner) {
        if (isWaiting && timeLeft === 0) {
          // Fast spin while waiting for winner announcement
          if (animationPhase !== 'fast-spin') {
            console.log('üé¨ Switching to fast-spin animation');
            animationPhase = 'fast-spin';
            carouselAnimation.stop();
            startFastSpinAnimation();
          }
        } else {
          // Normal carousel movement when not waiting
          if (animationPhase !== 'normal') {
            console.log('üé¨ Switching to normal animation');
            animationPhase = 'normal';
            carouselAnimation.stop();
            startNormalAnimation();
          }
          }

        // Continue checking for state changes
        animationLoopId = requestAnimationFrame(animate);
          return;
      }

      // Transition to slow-reveal when winner detected
      if (currentWinner && animationPhase !== 'slow-reveal') {
        if (process.env.NODE_ENV === 'development') {
          console.log('üîÑ Winner detected, calculating position...');
        }
      }

            // PHASE 2: Winner detected - use new simplified approach
      if (currentWinner && animationPhase !== 'slow-reveal') {
        animationPhase = 'slow-reveal';
        console.log('üéØ Winner detected, starting simplified landing animation');

        // Get bettors that match the carousel rendering
        const currentBettors = getCurrentBettors();
        const prevBettors = getPreviousBettors();
        const bettorsToShow = prevBettors.length > 0 ? prevBettors : currentBettors;

        if (bettorsToShow.length === 0) {
          console.log('‚ö†Ô∏è No bettors to animate - using fallback');
          setShowWinnerVisually(true);
          return;
        }

        // Create base set with minimum 6 items (same as rendering logic)
        const baseItems = [
          ...bettorsToShow,
          ...Array.from({ length: Math.max(0, 6 - bettorsToShow.length) }, () => null)
        ];

        // Find winner in the base items array
        const winnerIndex = baseItems.findIndex(bettor => 
          bettor && (
            bettor.address === currentWinner.winner ||
            bettor.address === currentWinner.fullAddress ||
            bettor.username === currentWinner.username ||
            bettor.displayName === currentWinner.displayName
          )
        );

        if (winnerIndex === -1) {
          console.log('‚ö†Ô∏è Winner not found in base items - using fallback');
          setShowWinnerVisually(true);
          return;
        }

        console.log('üéØ Winner found at index:', winnerIndex, 'in base items array');

        // Create repeated array (5 times for plenty of scroll distance)
        const REPEATS = 5;
        const repeatedBettors = Array(REPEATS).fill().flatMap(() => baseItems);

        // Find winner in last repeat (for maximum drama)
        const targetIndex = (REPEATS - 2) * baseItems.length + winnerIndex; // Second to last repeat

        // Calculate precise offset to center winner
        const itemWidth = scrollParams.cardWidth;
        const containerWidth = scrollParams.containerWidth;
        const winnerOffset = -(targetIndex * itemWidth - containerWidth / 2 + itemWidth / 2);

        console.log('üé∞ SIMPLIFIED WINNER CALCULATION:');
        console.log('  - Winner index in original array:', winnerIndex);
        console.log('  - Target index in repeated array:', targetIndex);
        console.log('  - Item width:', itemWidth);
        console.log('  - Container width:', containerWidth);
        console.log('  - Calculated winner offset:', winnerOffset);

        // Stop current animation and start winner landing
        carouselAnimation.stop();
        animateToWinner(winnerOffset).then(() => {
          setShowWinnerVisually(true);
          console.log('üé¨ Winner revealed after perfect landing!');
        });
      }

      // Winner animation is now handled by Framer Motion above
      // No need for manual animation frames when winner is being animated
      if (currentWinner && animationPhase === 'slow-reveal') {
        return; // Let Framer Motion handle the winner animation
      }
    };

    // Initialize motion value and start normal animation
    carouselX.set(0);

    // Start with normal animation immediately
    console.log('üé¨ Starting initial normal animation');

    // Start the animation logic loop first
    animationLoopId = requestAnimationFrame(animate);

    // Then start normal animation
    setTimeout(() => {
      startNormalAnimation();
    }, 100);

    // Reset animation when new round starts
    const resetAnimation = () => {
      console.log('üîÑ Resetting Framer Motion animation for new round');

      animationPhase = 'normal';
      winnerPositionFound = false;
      winnerCardVisible = false;
      targetPosition = null;
      cachedTargetArrow = null;
      setShowWinnerVisually(false);

      // Reset arrow states
      if (cachedTargetArrow) {
        cachedTargetArrow.classList.remove('winner-approaching', 'winner-landed');
      }

      // Stop any running animations and reset position
      carouselAnimation.stop();
      if (animationLoopId) {
        cancelAnimationFrame(animationLoopId);
      }
      carouselX.set(0); // Start from beginning

      // Restart normal animation
      startNormalAnimation();

      // Restart animation loop
      animationLoopId = requestAnimationFrame(animate);
    };

    // Expose reset function for external use - Enhanced for Telegram
    window.resetCarouselAnimation = resetAnimation;

    // OPTIMIZED: Minimal Telegram event listeners with throttling
    if (window.Telegram?.WebApp) {
      let throttleTimeout = null;

      // Throttled viewport change handler
      const handleTelegramViewportChange = () => {
        if (throttleTimeout) return; // Throttle to prevent spam
        throttleTimeout = setTimeout(() => {
          throttleTimeout = null;

          // CRITICAL: Recalculate scroll parameters for new viewport
          const oldCardWidth = scrollParams.cardWidth;
          scrollParams = calculateScrollParameters();

          console.log('üì± TELEGRAM VIEWPORT CHANGE:');
          console.log('  - Old card width:', oldCardWidth, 'px');
          console.log('  - New card width:', scrollParams.cardWidth, 'px');

          // Only reset if card width actually changed
          if (Math.abs(oldCardWidth - scrollParams.cardWidth) > 5) {
            console.log('üîÑ Card width changed significantly - resetting animation');
            if (window.resetCarouselAnimation && animationPhase === 'normal') {
              window.resetCarouselAnimation();
            }
          }
        }, 300); // Increased delay to reduce CPU usage
      };

      // Listen for Telegram WebApp events (only essential ones)
      if (window.Telegram.WebApp.onEvent) {
        window.Telegram.WebApp.onEvent('viewportChanged', handleTelegramViewportChange);
      }
    }

    return () => {
      // Stop Framer Motion animations and animation loop
      carouselAnimation.stop();
      if (animationLoopId) {
        cancelAnimationFrame(animationLoopId);
      }

      // Clean up global reference
      delete window.resetCarouselAnimation;

      // Clean up Telegram-specific event listeners
      if (window.Telegram?.WebApp) {
        console.log('üéØ Cleaning up Telegram WebApp event listeners');
        // Remove Telegram WebApp event listeners
        if (window.Telegram.WebApp.offEvent) {
          window.Telegram.WebApp.offEvent('viewportChanged');
        }
      }
    };
  }, []); // EMPTY dependency array to prevent re-initialization

  // Socket integration for immediate synchronization - SINGLE CONNECTION POINT
  useEffect(() => {
    console.log('üîå App.js: Establishing single socket connection for entire app...');
    socketService.connect();

    // Listen for timer updates from socket
    socketService.on('timer', (timerData) => {
      console.log('‚è∞ App: Socket timer update:', timerData);

      // SAFETY: Ensure timerData exists and has required properties
      if (!timerData || typeof timerData !== 'object') {
        console.warn('‚ö†Ô∏è Invalid timer data received in App:', timerData);
        return;
      }

      const newTimeRemaining = typeof timerData.timeRemaining === 'number' ? timerData.timeRemaining : timeRemaining;
      const newRoundNumber = typeof timerData.roundNumber === 'number' ? timerData.roundNumber : currentRound;

      // Only update timeRemaining if it's significantly different (>2 seconds) or new round
      setTimeRemaining(prev => {
        const timeDiff = Math.abs(prev - newTimeRemaining);
        const isNewRound = newRoundNumber !== currentRound;

        // Only clear winner state when new round detected AND timer is significantly higher (real new round)
        if (isNewRound && contractWinner && newTimeRemaining > 200) {
          console.log('üîÑ Timer update detected REAL new round with fresh timer - clearing winner state');
          console.log('üîì Exiting winner state - new round detected');
          setIsInWinnerState(false);
          setContractWinner(null);
          setShowWinnerAnnouncement(false);
          setShowWinnerVisually(false);
          setPostWinnerLoading(false);
          setIsAnyWinnerDisplayActive(false);
          setPreviousRoundBettors([]);
        }

        // Sync if: new round, big difference (>10s), or timer reset (server > client)
        if (isNewRound || timeDiff > 10 || newTimeRemaining > prev) {
          console.log('‚è∞ Syncing timer:', prev, '‚Üí', newTimeRemaining, isNewRound ? '(new round)' : timeDiff > 10 ? '(>10s difference)' : '(backend ahead)');
          return newTimeRemaining;
        }

        // Otherwise keep local countdown running smoothly
        return prev;
      });

      if (newRoundNumber && newRoundNumber !== currentRound) {
        setCurrentRound(newRoundNumber);
      }
      // Note: Contract state timer will be updated via fullGameUpdate/contractStateUpdate events
    });

    // Listen for new round events
    socketService.on('newRound', (roundData) => {
      console.log('üé∞ App: New round detected via socket:', roundData);
      console.log('üîì Exiting winner state - new round started');

      setTimeRemaining(roundData.timeRemaining);
      setCurrentRound(roundData.roundNumber);
      setIsInWinnerState(false);
      setIsAnyWinnerDisplayActive(false);
      winnerCoordinator.reset();
      lastWinnerTimestampRef.current = null; // Clear duplicate protection for new round

      // Clear ALL winner highlighting for new round
      setContractWinner(null);
      setShowWinnerAnnouncement(false);
      setShowWinnerVisually(false);
      setPreviousRoundBettors([]);
      setWaitingForWinner(false);
      setPostWinnerLoading(false);

      // OPTIMIZED: Simple animation reset
      if (window.resetCarouselAnimation) {
        window.resetCarouselAnimation();
      }
    });

    // Listen for game state updates
    socketService.on('gameState', (gameState) => {
      console.log('üéÆ App: Game state update:', gameState);

      // Priority: Use timer.timeRemaining ONLY (as user specified)
      const backendTimeRemaining = gameState.timer?.timeRemaining;

      if (backendTimeRemaining !== undefined) {
        setTimeRemaining(prev => {
          const timeDiff = Math.abs(prev - backendTimeRemaining);

          // Only sync if there's a significant difference (>10s) or if backend is ahead
          if (timeDiff > 10 || backendTimeRemaining > prev) {
            console.log('‚è∞ GameState syncing timer:', prev, '‚Üí', backendTimeRemaining, timeDiff > 10 ? '(>10s difference)' : '(backend ahead)');
            return backendTimeRemaining;
          }

          // Keep local countdown running if difference is small
          console.log('‚è∞ GameState keeping local timer:', prev, 'vs backend:', backendTimeRemaining);
          return prev;
        });
      }
      if (gameState.currentRound) {
        // Extract roundNumber if currentRound is an object, otherwise use the value directly
        const roundNumber = typeof gameState.currentRound === 'object' && gameState.currentRound.roundNumber 
          ? gameState.currentRound.roundNumber 
          : gameState.currentRound;

        // Check if this is a new round with active timer - this means new round started
        const isNewRound = roundNumber !== currentRound;
        const hasActiveTimer = gameState.timer?.isActive || gameState.timer?.timeRemaining > 60;

        if (isNewRound && hasActiveTimer && isInWinnerStateRef.current) {
          console.log('üîÑ GameState detected NEW ROUND with active timer - clearing winner state');
          console.log('üîì Exiting winner state - new round started via gameState');
          setIsInWinnerState(false);
          setContractWinner(null);
          setShowWinnerAnnouncement(false);
          setShowWinnerVisually(false);
          setPostWinnerLoading(false);
          setIsAnyWinnerDisplayActive(false);
          setPreviousRoundBettors([]);
          winnerCoordinator.reset();
        }

        setCurrentRound(roundNumber);
      }

      // Handle winner-related states
      if (gameState.isWaitingForWinner !== undefined) {
        // If transitioning TO waiting for winner, store current bettors
        if (gameState.isWaitingForWinner && !waitingForWinnerRef.current && gameBettorsRef.current.length > 0) {
          console.log('üéÆ App: Transitioning to waiting for winner, storing bettors:', gameBettorsRef.current);
          setPreviousRoundBettors([...gameBettorsRef.current]);
        }

        setWaitingForWinner(gameState.isWaitingForWinner);

        // If waitingForWinner becomes false AND we have a new round with active timer, reset winner state
        if (!gameState.isWaitingForWinner && isInWinnerStateRef.current) {
          const roundNumber = typeof gameState.currentRound === 'object' && gameState.currentRound.roundNumber 
            ? gameState.currentRound.roundNumber 
            : gameState.currentRound;
          const isNewRound = roundNumber && roundNumber !== currentRound;
          const hasActiveTimer = gameState.timer?.isActive || gameState.timer?.timeRemaining > 60;

          if (isNewRound && hasActiveTimer) {
            console.log('üîÑ waitingForWinner false + new round + active timer = new round started - clearing winner state');
            console.log('üîì Exiting winner state - new round confirmed');
            setIsInWinnerState(false);
            setContractWinner(null);
            setShowWinnerAnnouncement(false);
            setShowWinnerVisually(false);
            setPostWinnerLoading(false);
            setIsAnyWinnerDisplayActive(false);
            setPreviousRoundBettors([]);
            winnerCoordinator.reset();
          } else {
            console.log('‚è≥ waitingForWinner became false, but keeping winner state (no new round detected)');
          }
        }
      }

      // Update bettors data centrally (avoids duplicate processing)
      if (gameState.bettors && Array.isArray(gameState.bettors)) {
        // Only log if bettors count changed to reduce spam
        if (gameState.bettors.length !== gameBettorsRef.current.length) {
          console.log('üéÆ App: Updating bettors from gameState:', gameState.bettors.length, 'bettors');
          // Trigger slot machine animation when new bettor joins
          setSlotSpinning(true);
          setTimeout(() => setSlotSpinning(false), 3000);
        }

        // Store current bettors as previous round bettors when waiting for winner
        if (gameState.isWaitingForWinner && gameState.bettors.length > 0) {
          console.log('üéÆ App: Storing bettors for winner matching:', gameState.bettors);
          setPreviousRoundBettors([...gameState.bettors]);
        }

        setGameBettors(gameState.bettors);
      }

      // Check for winner announcement in game state (prevent duplicates with stronger protection)
      if (gameState.winnerAnnouncement) {
        showWinnerAnnouncements(gameState.winnerAnnouncement, 'gameState');
      }
    });

    // Listen for comprehensive game data updates
    socketService.on('fullGameUpdate', (gameData) => {
      console.log('üéÆ App: Full game update:', gameData);
      // These updates will trigger re-renders via useJackpotContract hook
      // The hook will detect the changes and update accordingly
    });

    // Listen for bettors updates
    socketService.on('bettorsUpdate', (data) => {
      console.log('üí∞ App: Bettors update:', data);
      // Trigger slot machine animation on bettor updates
      setSlotSpinning(true);
      setTimeout(() => setSlotSpinning(false), 3000);
      // Force a refresh of contract state to sync with socket data
      // This ensures jackpot value and user stats stay in sync
    });

    // Listen for waiting for winner events
    socketService.on('waitingForWinner', (data) => {
      console.log('üéØ App: Waiting for winner state changed:', data);

      // Handle both old boolean format and new object format
      const isWaiting = typeof data === 'boolean' ? data : data.isWaiting;
      const message = typeof data === 'object' ? data.message : null;

      // Don't reset winner state just because waitingForWinner changed
      // Only reset when we get explicit newRound/roundReset events
      setWaitingForWinner(isWaiting);

      // Log but don't auto-reset winner state
      if (!isWaiting && isInWinnerStateRef.current) {
        console.log('‚è≥ waitingForWinner event became false, but keeping winner state until explicit new round');
      }

      if (message) {
        console.log('üéØ App: Winner selection message:', message);
      }
    });

    // Listen for winner announcements (primary)
    socketService.on('winner', (winnerData) => {
      console.log('üèÜ App: Winner announced:', winnerData);
      console.log('üèÜ App: Current gameBettors for comparison:', gameBettors);
      console.log('üèÜ App: Previous round bettors for comparison:', previousRoundBettors);
      setWaitingForWinner(false);
      setPostWinnerLoading(true);

      // Use centralized manager
      showWinnerAnnouncements(winnerData, 'winner event');
    });

    // Listen for direct winner announcements (fallback/reliable delivery)
    socketService.on('winnerAnnouncement', (winnerData) => {
      console.log('üéØ App: Direct winner announcement:', winnerData);
      setWaitingForWinner(false);

      // Use centralized manager
      showWinnerAnnouncements(winnerData, 'direct announcement');
    });

    // Listen for round reset events (when timer is reset due to no bettors)
    socketService.on('roundReset', (resetData) => {
      console.log('üîÑ App: Round reset detected:', resetData);
      console.log('üîì Exiting winner state - round reset');

      // Reset timer to the new duration
      setTimeRemaining(resetData.timeRemaining);
      setCurrentRound(resetData.roundNumber);

      // Clear ALL winner states since this is a fresh timer
      setIsInWinnerState(false);
      setWaitingForWinner(false);
      setContractWinner(null);
      setShowWinnerAnnouncement(false);
      setShowWinnerVisually(false);
      setPostWinnerLoading(false);
      setIsAnyWinnerDisplayActive(false);
      winnerCoordinator.reset();

      // Clear bettors list since round was reset
      setGameBettors([]);
      setPreviousRoundBettors([]);

      // OPTIMIZED: Simple animation reset
      if (window.resetCarouselAnimation) {
        window.resetCarouselAnimation();
      }
    });

    return () => {
      console.log('üîå App.js: Cleaning up socket listeners...');
      socketService.off('timer');
      socketService.off('newRound');
      socketService.off('roundReset');
      socketService.off('gameState');
      socketService.off('fullGameUpdate');
      socketService.off('bettorsUpdate');
      socketService.off('waitingForWinner');
      socketService.off('winner');
      socketService.off('winnerAnnouncement');
      // Disconnect when app unmounts to prevent memory leaks
      socketService.disconnect();
    };
  }, [currentRound, showWinnerAnnouncements]); // Added missing dependencies

  const handleBetChange = (e) => {
    const inputValue = e.target.value;

    // Allow empty input or numbers with decimal points (both . and ,)
    if (inputValue === '' || inputValue === '.' || inputValue === ',' || /^\d*[.,]?\d*$/.test(inputValue)) {
      // Convert comma to period for consistency
      const normalizedValue = inputValue.replace(',', '.');

      // For empty or just "." input, keep the raw value but show comma if user typed comma
      if (normalizedValue === '' || normalizedValue === '.') {
        setBetAmount(inputValue); // Keep original input (might be comma)
        return;
      }

      // For valid numbers, check limits but preserve decimal typing
      const numericValue = parseFloat(normalizedValue);
      if (!isNaN(numericValue) && numericValue <= 10) {
        setBetAmount(inputValue); // Keep the original input format (comma or period)
      }
    }
  };

  const handleTabChange = (tab) => {
    hapticFeedback('light');
    setActiveTab(tab);
  };

  // Enhanced bet placement with real smart contract
  const handlePlaceBet = async () => {
    // Prevent betting during winner display period
    if (contractWinner || showWinnerAnnouncement) {
      showAlert('‚è≥ Please wait for the new round to start before placing a bet!');
      return;
    }

    if (!isConnected) {
      showAlert('Please connect your wallet first!');
      return;
    }

    // Convert comma to period before parsing for validation
    const normalizedBetAmount = betAmount.toString().replace(',', '.');
    const numericBetAmount = parseFloat(normalizedBetAmount);

    if (isNaN(numericBetAmount) || numericBetAmount <= 0) {
      showAlert('Please enter a valid bet amount!');
      return;
    }

    try {
      hapticFeedback('medium');
      await placeBet(numericBetAmount);

      // Reset bet amount after successful bet
      setBetAmount(0.11);

      hapticFeedback('success');
      showAlert(`‚úÖ Bet of ${numericBetAmount.toFixed(3)} TON placed successfully!`);
    } catch (error) {
      console.error('Failed to place bet:', error);
      hapticFeedback('error');
      showAlert(`‚ùå Failed to place bet: ${error.message}`);
    }
  };

  return (
    <div className="app">
        <div className="app-container">
          {/* Header Section */}
          <div className="header-section">
            <div className="header-top">
              <div className="logo-section">
                <div className="logo-icon">üé∞</div>
                <div className="app-name">SlotPot</div>
              </div>

              <div className="balance-section">
                <div className="balance-label">Balance</div>
                <div className="balance-amount">
                  {isConnected ? `${(contractState.userBalance || 0).toFixed(6)}` : '0.000000'}
                  <span className="balance-symbol">TON</span>
                </div>
              </div>

              <div className="header-icons">
                <WalletConnection />
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="main-content">
            {/* Jackpot Display */}
            <div className="jackpot-header">
              <div className="jackpot-title">üí∞ JACKPOT</div>
              <div className="jackpot-value-display">{jackpotValue.toFixed(3)}</div>
              <div className="jackpot-subtitle">Winner takes all...</div>
            </div>

            {/* Betting Section */}
            <div className="betting-section">
              <div className="bet-input-section">
                <input 
                  type="text" 
                  className="bet-amount-input" 
                  value={betAmount}
                  onChange={handleBetChange}
                  placeholder="0.000"
                  inputMode="decimal"
                />
                <button className="bet-increment-btn" onClick={() => setBetAmount(prev => {
                  const currentValue = parseFloat(prev) || 0;
                  return Math.min(currentValue + 0.1, 10).toFixed(1);
                })}>
                  +0.1
          </button>
              </div>
          <button 
                className="bet-button" 
                onClick={handlePlaceBet}
                disabled={isPlacingBet || !isConnected || contractWinner || showWinnerAnnouncement}
          >
                {contractWinner || showWinnerAnnouncement ? 'Winner Display - Wait for New Round' :
                 isPlacingBet ? 'Betting...' : 'Bet'}
          </button>
        </div>

            {/* Stats Grid */}
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-value">{userBetTotal.toFixed(3)}</div>
                <div className="stat-label">Your Wager</div>
              </div>
              <div className="stat-card chance-stat">
                <div className="stat-value">{userWinChance.toFixed(2)}%</div>
                <div className="stat-label">Your Chance</div>
          </div>
        </div>

            {/* Time Section */}
            <div className="time-section">
              <div className="time-label">Time Remaining </div>
              <div className="time-value">
                {Math.floor((timeRemaining || 0) / 60)}:{((timeRemaining || 0) % 60).toString().padStart(2, '0')}
          </div>
        </div>

            {/* Players Carousel Section */}
            <div className="players-carousel-section">
              <div className={`carousel-header ${
                (timeRemaining === 0 && waitingForWinner) || (contractWinner && !showWinnerVisually) ? 'analyzing' : 
                showWinnerVisually ? 'winner' : ''
              }`}>
                <h3>
                  {(timeRemaining === 0 && waitingForWinner) || (contractWinner && !showWinnerVisually) ? 
                    "üîç Analyzing Bets..." : 
                    showWinnerVisually && contractWinner ? 
                    `üèÜ Winner: ${contractWinner.username || contractWinner.displayName || contractWinner.winner || 'Player'}` :
                    `Players (${gameBettors.length > 0 ? gameBettors.length : previousRoundBettors.length})`
                  }
                </h3>
              </div>
              <div className="players-carousel">
                {/* TARGET ARROW - Cards must land here! */}
                <div className="carousel-target-arrow">‚¨áÔ∏è</div>

                <motion.div 
                  ref={carouselRef}
                  className={`carousel-track ${
                    (timeRemaining === 0 && waitingForWinner) || (contractWinner && !showWinnerVisually) ? 'analyzing' : 
                    showWinnerVisually ? 'winner-revealed' : 
                    slotSpinning ? 'sliding' : ''
                  }`}
                  animate={carouselAnimation}
                  style={{
                    x: carouselX
                  }}
                >
                  {(() => {
                    // For a fresh round, prefer empty display over old data
                    // Only use previousRoundBettors if we're in winner display mode
                    const bettorsToShow = gameBettors.length > 0 ? gameBettors : 
                                         (showWinnerVisually || waitingForWinner) ? previousRoundBettors : [];

                    console.log('üé† Using bettors:', bettorsToShow.length, 'total bettors');
                    console.log('üé† ContractWinner from backend:', contractWinner);

                    // Check if we're showing waiting cards due to empty data
                    if (bettorsToShow.length === 0) {
                      console.log('‚ö†Ô∏è SHOWING WAITING CARDS - No bettor data available');
                    }

                    // Create a base set with minimum 6 items (fill with waiting slots if needed)
                    const baseItems = [
                      ...bettorsToShow,
                      ...Array.from({ length: Math.max(0, 6 - bettorsToShow.length) }, () => null)
                    ];

                    // Use fake infinity approach: repeat the base set 5 times for long scrolling track
                    const REPEATS = 5;
                    const repeatedItems = Array(REPEATS).fill().flatMap(() => baseItems);

                    return repeatedItems.map((bettor, index) => {
                      // Winner highlighting - only show AFTER animation completes (showWinnerVisually)
                      const isWinner = showWinnerVisually && contractWinner && bettor && (
                        bettor.address === contractWinner.winner ||
                        bettor.address === contractWinner.fullAddress ||
                        bettor.username === contractWinner.username
                      );

                        return (
                          <div key={`${index}-${bettor?.address || 'waiting'}`} className={`carousel-card ${
                            isWinner ? 'winner' : 
                            bettor ? 'active' : 'waiting'
                          }`} data-debug={`winner: ${isWinner}, address: ${bettor?.address}`}>
                            <div className="mobile-player-avatar">
                              {bettor ? (
                                <div className="generated-avatar">
                                  {bettor.username ? bettor.username[0].toUpperCase() : '?'}
                                </div>
                              ) : (
                                <div className="waiting-icon">‚ùì</div>
                              )}
                            </div>
                            <div className="mobile-player-info">
                              <div className="mobile-player-name">
                                {bettor ? (bettor.username || bettor.displayName || 'Player') : 'Waiting'}
                              </div>
                              <div className="mobile-player-bet">
                                {bettor ? `${(bettor.amount || 0).toFixed(2)} TON` : '0.00 TON'}
                              </div>
                              {isWinner && contractWinner && contractWinner.prize && (
                                <div className="winner-prize">Won {parseFloat(contractWinner.prize).toFixed(3)} TON</div>
                              )}
                            </div>
                            {isWinner && (
                              <div className="winner-crown">üëë</div>
                            )}
                          </div>
                        );
                      });
                  })()}
                </motion.div>
              </div>
        </div>
      </div>

      {/* Mobile Footer */}
      <div className="mobile-footer">
            <div className="footer-stats">
              <div className="footer-stat">
                <div className="footer-stat-icon">üè†</div>
                <div className="footer-stat-value">Home</div>
              </div>
              <div className="footer-stat">
                <div className="footer-stat-icon">‚ö°</div>
                <div className="footer-stat-value">Fast</div>
            </div>
              <div className="footer-stat">
                <div className="footer-stat-icon">üïê</div>
                <div className="footer-stat-value">History</div>
            </div>
              <div className="footer-stat">
                <div className="footer-stat-icon">üîç</div>
                <div className="footer-stat-value">Search</div>
              </div>
          </div>
            <button className="games-button">
              <DollarSign size={14} />
            Games
          </button>
        </div>
      </div>

      {/* Contract Status Indicator */}
      {isLoadingContract && !contractWinner && !showWinnerAnnouncement && (
        <div className="loading-overlay">
          <div className="loading-spinner">üîÑ</div>
          <span>Loading contract data...</span>
        </div>
      )}

      {/* Winner Broadcast Component */}
      <WinnerBroadcast socketService={socketService} />
    </div>
  );
}

// Main App component with TON Connect Provider
function App() {
  return (
    <TonConnectUIProvider 
      manifestUrl={manifestUrl}
      uiPreferences={uiOptions.uiPreferences}
      language={uiOptions.language}
      restoreConnection={uiOptions.restoreConnection}
      actionsConfiguration={uiOptions.actionsConfiguration}
    >
      <AppContent />
    </TonConnectUIProvider>
  );
}

export default App;
