import React from 'react';
import { Wifi, WifiOff } from 'lucide-react';
import './OnlineIndicator.css';

const OnlineIndicator = ({ isConnected, onlineCount = 0 }) => {
  return (
    <div className="online-indicator">
      <div className="indicator-content">
        <div className="connection-status">
          {isConnected ? (
            <>
              <Wifi size={14} className="wifi-icon connected" />
              <span className="status-text">Online</span>
            </>
          ) : (
            <>
              <WifiOff size={14} className="wifi-icon disconnected" />
              <span className="status-text">Offline</span>
            </>
          )}
        </div>
        {isConnected && onlineCount > 0 && (
          <div className="online-count">
            <span className="count-text">{onlineCount} online</span>
          </div>
        )}
      </div>
    </div>
  );
};

export default OnlineIndicator;
