import React, { useRef, useEffect, useState, useCallback } from 'react';
import './SmoothWheel.css';
import { TonIcon } from './IconComponents';

const SmoothWheel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const wheelRef = useRef(null);
  const [spinState, setSpinState] = useState('idle'); // idle, spinning, landing, finished
  const [finalRotation, setFinalRotation] = useState(0);
  const [winnerIndex, setWinnerIndex] = useState(-1);
  const animationTimeoutRef = useRef(null);

  // Create an extended array of players for seamless looping
  const extendedPlayers = React.useMemo(() => {
    // If no players, create default waiting cards
    let playersToUse = players;
    if (!players || players.length === 0) {
      playersToUse = Array.from({ length: 8 }, (_, i) => ({
        id: `waiting-${i}`,
        username: 'Waiting',
        isRealPlayer: false,
        bet: 0
      }));
    }
    
    // Create 5 full sets of players for smooth infinite scroll effect
    const sets = 5;
    const extended = [];
    for (let i = 0; i < sets; i++) {
      extended.push(...playersToUse);
    }
    return extended;
  }, [players]);

  // Calculate the target position when winner is determined
  const calculateWinnerPosition = useCallback(() => {
    if (!winner || !players || players.length === 0) return -1;
    
    // Find winner in original players array
    let winnerPos = -1;
    const winnerUsername = winner?.username || winner?.displayName || winner?.name;
    const winnerAddress = winner?.address || winner?.winner || winner?.fullAddress;
    
    for (let i = 0; i < players.length; i++) {
      const player = players[i];
      if (!player || !player.isRealPlayer) continue;
      
      const isMatch = (player.address === winnerAddress) ||
                     (player.username === winnerUsername) ||
                     (player.displayName === winnerUsername) ||
                     (winnerAddress && player.address === winnerAddress);
      
      if (isMatch) {
        winnerPos = i;
        console.log('üéØ Found winner in players array at position:', i, 'Player:', player.username || player.displayName);
        break;
      }
    }
    
    return winnerPos;
  }, [winner, players]);

  // Start spinning animation
  const startSpin = useCallback(() => {
    if (!wheelRef.current || spinState !== 'idle') return;
    
    console.log('üé∞ Starting smooth wheel spin...');
    console.log('üé∞ Players data:', players);
    console.log('üé∞ Extended players:', extendedPlayers.length);
    setSpinState('spinning');
    
    // Add spinning class for continuous rotation
    wheelRef.current.classList.add('wheel-spinning');
    
  }, [spinState, players, extendedPlayers]);

  // Land on winner with smooth deceleration
  const landOnWinner = useCallback(() => {
    if (!wheelRef.current || !winner) return;
    
    const targetIndex = calculateWinnerPosition();
    if (targetIndex === -1) {
      console.error('‚ùå Could not find winner position');
      return;
    }
    
    console.log('üéØ Landing on winner at position:', targetIndex);
    setWinnerIndex(targetIndex);
    setSpinState('landing');
    
    // Remove spinning class
    wheelRef.current.classList.remove('wheel-spinning');
    
    // Calculate final rotation to land on winner
    // Each player card is 120px wide + 8px gap = 128px
    const cardWidth = 128;
    const centerOffset = wheelRef.current.offsetWidth / 2 - 60; // Center the winner card
    const targetPosition = -(targetIndex * cardWidth) + centerOffset;
    
    // Add multiple full rotations for dramatic effect (3-5 full spins)
    const fullRotations = 3 + Math.random() * 2; // 3-5 rotations
    const fullRotationDistance = players.length * cardWidth * fullRotations;
    const finalPos = targetPosition - fullRotationDistance;
    
    setFinalRotation(finalPos);
    
    // Apply the landing animation
    wheelRef.current.style.transition = 'transform 3.5s cubic-bezier(0.25, 0.1, 0.25, 1)';
    wheelRef.current.style.transform = `translateX(${finalPos}px)`;
    
    // Mark as finished after animation completes
    animationTimeoutRef.current = setTimeout(() => {
      setSpinState('finished');
      wheelRef.current.classList.add('wheel-finished');
      onWinnerLand?.();
      console.log('‚úÖ Wheel animation complete! Winner landed.');
    }, 3500);
    
  }, [winner, calculateWinnerPosition, players, onWinnerLand]);

  // Effect to handle spinning state changes
  useEffect(() => {
    if (isSpinning && spinState === 'idle') {
      startSpin();
    } else if (!isSpinning && spinState === 'spinning' && winner) {
      // Delay the landing slightly for better UX
      setTimeout(() => {
        landOnWinner();
      }, 1000);
    }
  }, [isSpinning, winner, spinState, startSpin, landOnWinner]);

  // Cleanup timeouts
  useEffect(() => {
    return () => {
      if (animationTimeoutRef.current) {
        clearTimeout(animationTimeoutRef.current);
      }
    };
  }, []);

  // Reset wheel when new round starts
  useEffect(() => {
    if (!isSpinning && !winner && spinState !== 'idle') {
      console.log('üîÑ Resetting wheel for new round');
      setSpinState('idle');
      setWinnerIndex(-1);
      setFinalRotation(0);
      
      if (wheelRef.current) {
        wheelRef.current.classList.remove('wheel-spinning', 'wheel-finished');
        wheelRef.current.style.transition = 'transform 0.5s ease';
        wheelRef.current.style.transform = 'translateX(0px)';
      }
    }
  }, [isSpinning, winner, spinState]);

  return (
    <div className="smooth-wheel-container">
      {/* Target Arrow */}
      <div className={`wheel-arrow ${spinState === 'finished' ? 'winner-landed' : ''}`}>
        ‚¨áÔ∏è
      </div>
      
      {/* Spinning Wheel */}
      <div className="wheel-viewport">
        <div 
          ref={wheelRef}
          className={`wheel-track ${spinState}`}
        >
          {extendedPlayers.map((player, index) => (
            <div
              key={`${index}-${player?.address || index}`}
              className={`wheel-card ${
                !player?.isRealPlayer ? 'waiting' : ''
              } ${
                winnerIndex !== -1 && (index % players.length) === winnerIndex ? 'winner' : ''
              }`}
            >
              <div className="wheel-player-avatar">
                {player?.isRealPlayer ? (
                  <div className="telegram-avatar">
                    {player.username?.charAt(0)?.toUpperCase() || player.displayName?.charAt(0)?.toUpperCase() || 'üë§'}
                  </div>
                ) : (
                  <div className="waiting-icon">‚ùì</div>
                )}
              </div>
              
              <div className="wheel-player-info">
                <div className="wheel-player-name">
                  {player?.isRealPlayer ? (player.username || player.displayName || 'Player') : 'Waiting'}
                </div>
                <div className="wheel-player-bet">
                  {player?.isRealPlayer ? (
                    <>
                      <TonIcon className="wheel-ton-icon" />
                      {player.bet?.toFixed(3) || '0.000'}
                    </>
                  ) : (
                    '---'
                  )}
                </div>
              </div>
              
              {/* Winner Crown */}
              {winnerIndex !== -1 && (index % players.length) === winnerIndex && spinState === 'finished' && (
                <div className="winner-crown">üëë</div>
              )}
            </div>
          ))}
        </div>
      </div>
      
      {/* Gradient Fade Edges */}
      <div className="wheel-fade-left"></div>
      <div className="wheel-fade-right"></div>
    </div>
  );
};

export default SmoothWheel;
