import React, { useRef, useEffect, useState, useCallback } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay } from 'swiper/modules';
import 'swiper/css';
import './SimpleCarousel.css';

const SimpleCarousel = ({ 
  players, 
  winner, 
  isSpinning, 
  onWinnerLand 
}) => {
  const swiperRef = useRef(null);
  const [isCustomSpinning, setIsCustomSpinning] = useState(false);
  const [spinSpeed, setSpinSpeed] = useState(50);
  const [showingWinner, setShowingWinner] = useState(false);
  const animationRef = useRef(null);
  const targetIndexRef = useRef(null);
  const spinPhaseRef = useRef('idle'); // 'idle', 'spinning', 'slowing', 'stopped', 'displaying_winner'
  const winnerTimeoutRef = useRef(null);

  // Handle swiper initialization
  const handleSwiperInit = (swiper) => {
    swiperRef.current = swiper;
  };

  // Natural casino-style spin animation
  const startCasinoSpin = useCallback(() => {
    if (!swiperRef.current || isCustomSpinning) return;

    console.log('🎰 Starting casino-style spin animation');
    setIsCustomSpinning(true);
    setShowingWinner(false);
    spinPhaseRef.current = 'spinning';

    // Stop any existing autoplay
    if (swiperRef.current.autoplay) {
      swiperRef.current.autoplay.stop();
    }

    // Start fast spinning
    let currentSpeed = 20; // Start very fast
    setSpinSpeed(currentSpeed);

    const spinInterval = setInterval(() => {
      if (swiperRef.current && spinPhaseRef.current === 'spinning') {
        // Get current slide index
        const activeIndex = swiperRef.current.activeIndex;
        // Move to next slide
        swiperRef.current.slideNext(currentSpeed);
      }
    }, currentSpeed);

    // Save interval reference for cleanup
    animationRef.current = spinInterval;

    return spinInterval;
  }, [isCustomSpinning]);

  // Natural deceleration to winner
  const startSlowDownToWinner = useCallback((winnerIndex) => {
    if (!swiperRef.current || spinPhaseRef.current !== 'spinning') return;

    console.log('🎯 Starting slow-down to winner at index:', winnerIndex);
    spinPhaseRef.current = 'slowing';

    // Clear fast spinning
    if (animationRef.current) {
      clearInterval(animationRef.current);
    }

    // Calculate how many slides to go past winner before slowing down
    const currentIndex = swiperRef.current.activeIndex % players.length;
    const overshoot = 8; // Go 8 slides past winner for natural feel
    let targetSlide = winnerIndex + overshoot;

    // If we need to loop around
    if (targetSlide >= players.length) {
      targetSlide = targetSlide % players.length;
    }

    // Start deceleration animation
    let speed = 50; // Start medium speed
    let currentPos = currentIndex;

    const decelerateInterval = setInterval(() => {
      if (!swiperRef.current) {
        clearInterval(decelerateInterval);
        return;
      }

      // Move to next slide
      swiperRef.current.slideNext(speed);
      currentPos = (currentPos + 1) % players.length;

      // Check if we're getting close to target
      const distanceToTarget = Math.abs(currentPos - winnerIndex);
      const isClose = distanceToTarget <= 2 || distanceToTarget >= players.length - 2;

      if (isClose && speed > 800) {
        // We're close to winner and moving slowly enough - final positioning
        clearInterval(decelerateInterval);

        // Final slide to exact winner position
        setTimeout(() => {
          if (swiperRef.current) {
            swiperRef.current.slideTo(winnerIndex, 1000); // Smooth final slide

            // COMPLETELY STOP ALL MOVEMENT
            setTimeout(() => {
              if (swiperRef.current) {
                // Stop autoplay completely
                if (swiperRef.current.autoplay) {
                  swiperRef.current.autoplay.stop();
                }

                // Disable autoplay entirely during winner display
                swiperRef.current.autoplay.running = false;

                spinPhaseRef.current = 'displaying_winner';
                setIsCustomSpinning(false);
                setShowingWinner(true);

                console.log('🏆 Winner display started - ALL MOVEMENT STOPPED');

                // Trigger winner landed callback
                setTimeout(() => {
                  onWinnerLand?.();
                }, 500);

                // Keep showing winner for 8 seconds before allowing normal operation
                winnerTimeoutRef.current = setTimeout(() => {
                  console.log('🔄 Winner display timeout - resuming normal operation');
                  spinPhaseRef.current = 'idle';
                  setShowingWinner(false);

                  // Re-enable autoplay after winner display
                  if (swiperRef.current && swiperRef.current.autoplay && !winner) {
                    swiperRef.current.autoplay.running = true;
                    swiperRef.current.autoplay.start();
                  }
                }, 8000); // Show winner for 8 seconds
              }
            }, 1200); // Wait for final slide to complete
          }
        }, 200);
      } else {
        // Gradually slow down (realistic deceleration)
        speed = Math.min(speed * 1.08, 800); // Exponential slowdown, max 800ms
        setSpinSpeed(speed);
      }
    }, speed);

    animationRef.current = decelerateInterval;
  }, [players.length, onWinnerLand, winner]);

  // Handle winner announcement with realistic spin
  useEffect(() => {
    if (winner && swiperRef.current && players && !isCustomSpinning && !showingWinner) {
      const winnerIndex = players.findIndex(p => 
        p && (
          p.address === winner.address || 
          p.username === winner.username ||
          p.address === winner.winner ||
          p.address === winner.fullAddress ||
          p.username === winner.displayName
        )
      );

      if (winnerIndex !== -1) {
        targetIndexRef.current = winnerIndex;
        console.log('🏆 Winner found at index:', winnerIndex, 'Starting casino spin...');

        // Clear any existing winner timeout
        if (winnerTimeoutRef.current) {
          clearTimeout(winnerTimeoutRef.current);
        }

        // Start spinning, then slow down to winner after 2-3 seconds
        const spinInterval = startCasinoSpin();

        setTimeout(() => {
          startSlowDownToWinner(winnerIndex);
        }, 2500); // Spin for 2.5 seconds before starting to slow down
      }
    }
  }, [winner, players, isCustomSpinning, showingWinner, startCasinoSpin, startSlowDownToWinner]);

  // Handle regular spinning state (not winner selection)
  useEffect(() => {
    // Don't start autoplay if we're showing winner
    if (showingWinner || spinPhaseRef.current === 'displaying_winner') {
      if (swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.stop();
      }
      return;
    }

    if (isSpinning && !winner && !isCustomSpinning && !showingWinner) {
      // Regular fast spinning while waiting for winner
      if (swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.start();
      }
    } else if (!isSpinning && !winner && !isCustomSpinning && !showingWinner && spinPhaseRef.current === 'idle') {
      // Normal slow movement when not spinning and not showing winner
      if (swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.start();
      }
    }
  }, [isSpinning, winner, isCustomSpinning, showingWinner]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (animationRef.current) {
        clearInterval(animationRef.current);
      }
      if (winnerTimeoutRef.current) {
        clearTimeout(winnerTimeoutRef.current);
      }
    };
  }, []);

  // Prevent stopping when new players join during spin or winner display
  useEffect(() => {
    // Don't restart autoplay if we're in custom spinning mode or showing winner
    if (isCustomSpinning || showingWinner || spinPhaseRef.current !== 'idle') {
      // Force stop autoplay during winner display
      if (showingWinner && swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.stop();
        swiperRef.current.autoplay.running = false;
      }
      return;
    }

    // Only restart normal autoplay if we're not in winner selection mode
    if (!winner && swiperRef.current && swiperRef.current.autoplay) {
      swiperRef.current.autoplay.running = true;
      swiperRef.current.autoplay.start();
    }
  }, [players.length, isCustomSpinning, showingWinner, winner]);

  // Reset states when winner prop is cleared (new round)
  useEffect(() => {
    if (!winner && showingWinner) {
      console.log('🔄 Winner cleared - resetting carousel states');
      setShowingWinner(false);
      spinPhaseRef.current = 'idle';

      // Clear any pending winner timeout
      if (winnerTimeoutRef.current) {
        clearTimeout(winnerTimeoutRef.current);
        winnerTimeoutRef.current = null;
      }

      // Resume normal autoplay
      if (swiperRef.current && swiperRef.current.autoplay) {
        swiperRef.current.autoplay.running = true;
        swiperRef.current.autoplay.start();
      }
    }
  }, [winner, showingWinner]);

  return (
    <div className="simple-carousel-container">
      {/* Target Arrow */}
      <div className={`carousel-arrow ${
        isCustomSpinning && spinPhaseRef.current === 'slowing' ? 'winner-approaching' : 
        spinPhaseRef.current === 'displaying_winner' || showingWinner ? 'winner-landed' : ''
      }`}>⬇️</div>

      <Swiper
        ref={swiperRef}
        modules={[Autoplay]}
        spaceBetween={10}
        slidesPerView="auto"
        centeredSlides={true}
        loop={true}
        loopedSlides={players.length * 6} // More slides for smoother infinite scroll
        autoplay={{
          delay: isSpinning && !isCustomSpinning && !showingWinner ? 15 : 400, // Very fast when spinning, normal otherwise
          disableOnInteraction: false,
          pauseOnMouseEnter: false,
        }}
        speed={isCustomSpinning ? spinSpeed : (isSpinning && !showingWinner ? 30 : 300)}
        className={`player-swiper ${(isSpinning || isCustomSpinning) && !showingWinner ? 'spinning' : ''}`}
        allowTouchMove={false}
        onSwiper={handleSwiperInit}
      >
        {/* Create 6 sets for much smoother infinite scroll */}
        {[...Array(6)].map((_, setIndex) => 
          players.map((player, index) => (
            <SwiperSlide key={`${setIndex}-${player?.address || `waiting-${index}`}`} className="player-slide">
              <div className={`player-card ${
                winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) ? 'winner' : player ? 'active' : 'waiting'
              }`}>
                <div className="player-avatar">
                  {player ? (
                    <div className="player-avatar-content">
                      {player.username ? player.username[0].toUpperCase() : '?'}
                    </div>
                  ) : (
                    <div className="waiting-icon">❓</div>
                  )}
                </div>
                <div className="player-info">
                  <div className="player-name">
                    {player ? (player.username || player.displayName || 'Player') : 'Waiting'}
                  </div>
                  <div className="player-bet">
                    {player ? `${(player.amount || 0).toFixed(2)} TON` : '0.00 TON'}
                  </div>
                  {winner && player && (
                    player.address === winner.address || 
                    player.username === winner.username ||
                    player.address === winner.winner ||
                    player.address === winner.fullAddress ||
                    player.username === winner.displayName
                  ) && (
                    <div className="winner-prize">
                      Won {parseFloat(winner.prize || 0).toFixed(3)} TON
                    </div>
                  )}
                </div>
                {winner && player && (
                  player.address === winner.address || 
                  player.username === winner.username ||
                  player.address === winner.winner ||
                  player.address === winner.fullAddress ||
                  player.username === winner.displayName
                ) && (
                  <div className="winner-crown">👑</div>
                )}
              </div>
            </SwiperSlide>
          ))
        )}
      </Swiper>
    </div>
  );
};

export default SimpleCarousel;
